INSERT INTO article (id, articleid, availableoutsidepublication, content, documentsource, lastchangeddate, lastpublicationdate, prologue, sourcedate, sourceurl, title, type, updateallowed, version, lasteditedby_id, publication_id, numberoftimesviewed) VALUES (95, '1407648613', false, 'TWEEDE KAMER<P></P>DEN HAAG (ANP) - De SP in de Tweede Kamer wil dat jeugdige en oudere reizigers in het openbaar vervoer, ook na het afschaffen van de ''gewone'' strippenkaart, kunnen blijven beschikken over de roze strippenkaart. Via die roze strippenkaart kunnen zij met korting reizen.  <P></P>Donderdagmiddag is er nog een debat in de Kamer over het stoppen met de strippenkaart ten gunste van de <span ID="hitDiv2"><b>ov</b></span>-<span ID="hitDiv3"><b>chipkaart</b></span>. Minister Melanie Schultz van Haegen (Infrastructuur) wil dat het papieren reisbewijs tot het verleden gaat behoren. SP en PVV willen dat niet omdat de <span ID="hitDiv4"><b>chipkaart</b></span> duurder en fraudegevoeliger zou zijn.<P></P>Onafhankelijk van de uitkomst van het spoeddebat komt SP-Kamerlid Farshad Bashir met een motie waarin vervoerregio''s wordt gevraagd om in ieder geval de roze strippenkaart overeind te houden. GroenLinks pleitte in het verleden juist voor een roze <span ID="hitDiv5"><b>ov</b></span>-<span ID="hitDiv6"><b>chipkaart</b></span>. ', 'Algemeen Nederlands Persbureau ANP', NULL, NULL, '<p>TWEEDE KAMER</p><p>DEN HAAG (ANP) - De SP in de Tweede Kamer wil dat jeugdige en oudere reizigers in het openbaar vervoer, ook na het afschaffen van de ''gewone'' strippenkaart, kunnen blijven beschikken over de roze strippenkaart. Via die roze strippenkaart kunnen zij met korting reizen.</p>', '2011-04-28 00:00:00', NULL, 'SP wil behoud roze strippenkaart', 'Lexisnexis', false, 0, NULL, NULL, 0);
