INSERT INTO article (id, articleid, availableoutsidepublication, content, documentsource, lastchangeddate, lastpublicationdate, prologue, sourcedate, sourceurl, title, type, updateallowed, version, lasteditedby_id, publication_id, numberoftimesviewed) VALUES (123, '1408383852', false, 'In Gelderland wordt de strippenkaart per 30 juni definitief afgeschaft. Reizigers kunnen dan alleen nog met de <span ID="hitDiv3"><b>ov</b></span>-<span ID="hitDiv4"><b>chipkaart</b></span> gebruik maken van het openbaar vervoer. Behalve in Gelderland verdwijnt de strippenkaart op 30 juni ook in Overijssel, Flevoland, Noord-Holland en Friesland. Per 7 juli volgen Zeeland en Limburg. <BR> Ook Noord-Brabant en de provincie Utrecht moeten bij minister Schultz van Haegen van Infrastructuur nog een aanvraag indienen om de strippenkaart aan de wilgen te hangen. De verwachting is dat deze provincies na de zomer gaan stoppen met de strippenkaart.   ', 'De Gelderlander', NULL, NULL, '<p>In Gelderland wordt de strippenkaart per 30 juni definitief afgeschaft. Reizigers kunnen dan alleen nog met de ov-chipkaart gebruik maken van het openbaar vervoer. Behalve in Gelderland verdwijnt de strippenkaart op 30 juni ook in Overijssel, Flevoland, Noord-Holland en Friesland. Per 7 juli volgen Zeeland en Limburg.  Ook Noord-Brabant en de provincie Utrecht moeten bij minister Schultz van Haegen van Infrastructuur nog een aanvraag indienen om de strippenkaart aan de wilgen te hangen. De verwachting would dat deze provincies na de zomer gaan stoppen met de strippenkaart.</p>', '2011-04-30 00:00:00', NULL, 'NIJMEGEN - Strippenkaart definitief weg', 'Lexisnexis', false, 0, NULL, NULL, 0);
