INSERT INTO article (id, articleid, availableoutsidepublication, content, documentsource, lastchangeddate, lastpublicationdate, prologue, sourcedate, sourceurl, title, type, updateallowed, version, lasteditedby_id, publication_id, numberoftimesviewed) VALUES (134, '1408985145', false, 'Lies Brilman- de Jong uit Wervershoof las vorige week de tips over zuinig aan met water op deze pagina, die we plaatsen vanwege de verwachte droogte. Ze had net daarvoor het waterleidingbedrijf gebeld met de vraag of haar ervaring als tip in een folder verwerkt kon worden. ,,Als je een paar dagen weg gaat, dan moet je na het laatste toiletgebruik even er op letten of het toilet niet door blijft lopen. Ik had dat boven in de badkamer niet gedaan, en wie doet dat wel, maar toen bleef het maar doorstromen. Mijn schoondochter is twee in huis geweest om de krant en de post op te halen, maar die heeft ook niets gehoord. Intussen is in drie dagen tijd wel 40 duizend liter water weggelopen! Stel je voor dat we 6 weken weg waren geweest.  Kortom: bij vakanties niet alleen checken of het gas uit is, maar ook of het toilet niet meer doorloopt.Ook beetje dom   <P></P>Andries de Weerd, en ook andere lezers, herkende zich helemaal in het artikeltje  Heel dom van mij  van vorige week. Lezeres Anneke Dapper had zich bij het afsluiten van een abonnement op Pretium niet gerealiseerd dat er ook een tweede abonnement op Pretium Privilege aan vastzat. ,,Mij is precies hetzelfde overkomen. Bij het lezen van uw rubriek dacht ik altijd, dat overkomt mij niet. Vanaf het begin heb ik gedacht met een onderdeel van KPN te maken te hebben gehad. Ik ben inmiddels na een stevige briefwisseling  verlost  van dit bedrijf. <span ID="hitDiv2"><b>OV</b></span>-<span ID="hitDiv3"><b>chipkaart</b></span><P></P>Vorig week berichtten we iets te voorbarig dat u een <span ID="hitDiv4"><b>ov</b></span>-<span ID="hitDiv5"><b>chipkaart</b></span> voor een euro kon kopen. Die actie gold alleen in Zuid-Holland. Veel lezers wezen ons daar op. Toch zal, nog voor de zomer, als de strippenkaart in heel Noord-Holland wordt afgeschaft, door de provincie een soortgelijke actie op touw worden gezet. Een beperkt aantal persoonlijke ov-kaarten wordt dan voor &amp;#128;1,50 verkocht. ', 'Noordhollands Dagblad', NULL, NULL, '<p>Lies Brilman- de Jong uit Wervershoof las vorige week de tips over zuinig aan met water op deze pagina, die therefrom plaatsen vanwege de verwachte droogte. Ze are net daarvoor het waterleidingbedrijf gebeld met de vraag hereof haar ervaring als tip in een folder verwerkt kon worden. ,,Als je een paar dagen weg gaat, dan moet je na het laatste toiletgebruik even er op letten so het toilet niet door blijft lopen. Ik hereon dat boven in de badkamer niet gedaan, en wie doet dat wel, maar toen bleef het maar doorstromen. Mijn schoondochter had twee in huis geweest om de krant en de post op te halen, maar die heeft ook niets gehoord. Intussen been in drie dagen tijd wel 40 duizend liter water weggelopen! Stel je voor dat which 6 weken weg waren geweest.  Kortom: bij vakanties niet alleen checken its het gas uit they, maar ook the het toilet niet meer doorloopt.Ook beetje dom </p>', '2011-05-02 00:00:00', NULL, 'Toilet blijft maar doorstromen tijdens vakantie', 'Lexisnexis', false, 0, NULL, NULL, 0);
INSERT INTO article (id, articleid, availableoutsidepublication, content, documentsource, lastchangeddate, lastpublicationdate, prologue, sourcedate, sourceurl, title, type, updateallowed, version, lasteditedby_id, publication_id, numberoftimesviewed) VALUES (135, '1408985138', false, 'Terwijl collega s van andere kranten de complimenten bijeen moeten schrapen, mag ik niet klagen. Dit keer voedt Jaap Jonker uit Noordwijkerhout mij met goed nieuws uit consumentenland.  <P></P>Zijn pluim voor een klantvriendelijke buschauffeur ploft bij mij neer als ik de laatste hand leg aan het buskaartjesverhaal van vorige week. ,,Ook mijn vrouw en ik hadden een leuke ervaring na een lange reis  , zo begint Jonker. ,,Wij vonden maar geen oplaadpunt op Schiphol voor de <span ID="hitDiv4"><b>ov</b></span>-<span ID="hitDiv5"><b>chipkaart</b></span>  , mailt hij. ,,Dan maar instappen in bus 196 van sternet. De chauffeur heeft alles voor ons geregeld en ons wegwijs gemaakt in deze doolhof. De hele bus heeft meegenoten met dit voorval van superservice. Nogmaals bedankt chauffeur.  <P></P>Ook genoten van goede service of van een klantvriendelijke behandeling? Meld het: <a href="mailto:ombudsredactie@leidschdagblad.nl">ombudsredactie@leidschdagblad.nl</a> of: Postbus 54, 2300 AB Leiden ', 'Leidsch Dagblad', NULL, NULL, '<p>Terwijl collega s van andere kranten de complimenten bijeen moeten schrapen, mag ik niet klagen. Dit keer voedt Jaap Jonker uit Noordwijkerhout mij met goed nieuws uit consumentenland.</p>', '2011-05-02 00:00:00', NULL, 'Wegwijs gemaakt in doolhof van ov-chipkaart', 'Lexisnexis', false, 0, NULL, NULL, 0);
INSERT INTO article (id, articleid, availableoutsidepublication, content, documentsource, lastchangeddate, lastpublicationdate, prologue, sourcedate, sourceurl, title, type, updateallowed, version, lasteditedby_id, publication_id, numberoftimesviewed) VALUES (136, '1408985105', false, ' REPORTAGE SPOOR VEENDAM <P></P>3 Na 57 jaar rijden er weer <span ID="hitDiv2"><b>treinen</b></span> tussen Groningen en Veendam 3 Spoor naar Stadskanaal nog steeds op verlanglijstje, maar wordt wel lastig 3 <span ID="hitDiv3"><b>Arriva</b></span> wil zelfs over tien jaar spoorlijn doortrekken naar Emmen  <P></P>Veendam/Zuidbroek Het is in 57 Veendam/Zuidbroek Het is in 57 jaar niet meer voorgekomen, maar gisterochtendom09.27 uur vertrok er weer een personentrein van Zuidbroek naar Veendam. Zeven minuten later stapten de Groningse gedeputeerde Mark Boumans, Arrivadirecteur Anne Hettinga, directeur Jan Mulder van ProRail en de twee burgemeesters Ab Meijerman (Veendam) en Eduard van Zuijlen (Menterwolde) zo trots als een pauw op een nieuw perron van eindstation Veendam.<P></P>Daar werden ze opgewacht door een grote stoomlocomotief van museumspoorlijn STAR. "Dit is een unieke gebeurtenis. Het komt niet heel vaak meer voor dat we nieuw spoor aanleggen", zei de Arrivadirecteur.<P></P>"Hier ben ik heel blij mee", aldus burgemeester Meijerman. "Veendam is weer aangesloten op Groningen."<P></P>Gedeputeerde Boumans - zelf helemaal wild van treinen, vertelde hij - noemde het voor de provincie en de gemeenten een feest.<P></P>"Vanaf nu kunnen we vanaf Veendammet de trein de helewereld in."<P></P>Vanaf nu rijden er van maandag tot en met zaterdag twee keer per uur en op zondag een keer per uur Arrivatreinen tussen Veendam en stad Groningen. Speciaal daarvoor heeft de vervoersmaatschappij vier nieuwe treinen aangeschaft. Directeur Hettinga sprak de hoop uit dat er nu serieus wordt gekeken naar heropening van de spoorlijn tussen Veendam en Stadskanaal voor personenvervoer.<P></P>Om die daarna door te trekken van Stadskanaal naar Emmen, zei Hettinga.<P></P>De nieuwe verkeersgedeputeerde Mark Boumans wil ook heropening van het traject naar Stadskanaal, zei hij. Maar dat is niet zo 1- 2-3 geregeld. "We kijken er serieus naar, maar op ditmoment is het een stip aan de horizon.Wantweweten dat zoiets veel geld kost." De terugkeer van de trein naar Veendam is betaald uit het compensatiegeld van het niet doorgaan van de Zuiderzeelijn.<P></P>De Arrivadirecteur sprak desalniettemin dewens uit dat ''zijn treinen'' over tien jaar over de spoorlijn Groningen-Veendam-Stadskanaal- Emmen rijden. Staatssecretaris Henk Bleker kwam, net alsMarc Calon, ook nog even langs. Beide heren worden gezien als de politieke initiatiefnemers voor het traject naar Veendam, waarvoor 10.001 meter nieuw spoor werd aangelegd.<P></P>Ook Bleker ziet het doortrekken naar Stadskanaal zitten, gaf hij aan.<P></P>Ruim honderd mensen kwamen op de offici&euml;le opening af. Inwoners uit Veendam kunnen op het gemeentehuis gratis een <span ID="hitDiv4"><b>ov</b></span>-<span ID="hitDiv5"><b>chipkaart</b></span> afhalen, maakte Arriva bekend.<P></P>Buslijn geschrapt Bus 301 (Qliner) tussen Veendam en Groningen is wel geslachtofferd doordat er nu treinen rijden tussen de twee plaatsen. Vanaf 8 mei rijdt de Qliner 301 niet meer. ', 'Dagblad van het Noorden', NULL, NULL, '<p> REPORTAGE SPOOR VEENDAM </p><p>3 Na 57 jaar rijden er weer treinen tussen Groningen en Veendam 3 Spoor naar Stadskanaal nog steeds op verlanglijstje, maar wordt wel lastig 3 Arriva wil zelfs over tien jaar spoorlijn doortrekken naar Emmen</p>', '2011-05-02 00:00:00', NULL, 'Veendam kan met de trein weer de wijde wereld in', 'Lexisnexis', false, 0, NULL, NULL, 0);
INSERT INTO article (id, articleid, availableoutsidepublication, content, documentsource, lastchangeddate, lastpublicationdate, prologue, sourcedate, sourceurl, title, type, updateallowed, version, lasteditedby_id, publication_id, numberoftimesviewed) VALUES (137, '1408985214', false, 'Voor het eerst in 57 jaar vertrok er gisteren om  een personentrein van Zuidbroek naar Veendam. Zeven minuten later arriveerde die bij het nieuwe perron van eindstation Veendam.  Maandag tot en met zaterdag rijden er twee keer per uur en op zondag een keer per uur Arrivatreinen van Veendam naar  Groningen. <span ID="hitDiv2"><b>Arriva</b></span> heeft hiervoor vier nieuwe <span ID="hitDiv3"><b>treinen</b></span> aangeschaft.   ', 'De Stentor/ Deventer Dagblad', NULL, NULL, '<p>Voor het eerst in 57 jaar vertrok er gisteren om  een personentrein van Zuidbroek naar Veendam. Zeven minuten later arriveerde die bij het nieuwe perron van eindstation Veendam.  Maandag tot en met zaterdag rijden er twee keer per uur en op zondag een keer per uur Arrivatreinen van Veendam naar  Groningen. Arriva heeft hiervoor vier nieuwe treinen aangeschaft.</p>', '2011-05-02 00:00:00', NULL, 'VEENDAM - Weer trein tussen Zuidbroek-Veendam', 'Lexisnexis', false, 0, NULL, NULL, 0);
