INSERT INTO article (id, articleid, availableoutsidepublication, content, documentsource, lastchangeddate, lastpublicationdate, prologue, sourcedate, sourceurl, title, type, updateallowed, version, lasteditedby_id, publication_id, numberoftimesviewed) VALUES (250, '1415910199', false, 'Brussel<P></P>Fiets ontlenen met metrokaart<P></P>Wie in Brussel een fiets wil huren van fietsverhuursysteem Villo! kan dat vanaf nu ook met zijn metroabonnement of MOBIB-kaart. De Brusselse vervoersmaatschappij MIVB en Villo! hebben daarover een akkoord ondertekend.  <P></P>De MOBIB-kaart moet op termijn de papieren rittenkaarten vervangen. Vandaag gebruiken zo''n 675.000 reizigers de <span ID="hitDiv2"><b>chipkaart</b></span>. Vanaf nu kan deze ook gebruikt worden om een Villo! uit te lenen. Via de website <a href="http://www.villo.be">www.villo.be</a> schrijven MIVB-reizigers zich in om zo hun Villo!-abonnement op de MOBIB-kaart te laden. De klant krijgt vervolgens een aantal codes toegestuurd die moeten ingevoerd worden bij het volgende gebruik van de Villo!-fiets. Het eerste halfuur met de fiets is gratis, wie langer op het zadel zit, betaalt 50 cent voor het volgende halfuur, het volgende kost 1 euro. Villo! heeft momenteel 20.000 abonnees en 2.500 fietsen in 180 stations over heel het Brussels gewest. De fietsen zijn 24 uur op 24 beschikbaar.<P></P>Het project past in de strategie van de MIVB om de MOBIB-kaart uit te laten groeien tot een "echte mobiliteitsportefeuille". De openbaarvervoermaatschappij ging daarvoor onlangs ook in zee met Cambio-carsharing, dat sinds september ook op de MOBIB-kaart kan worden geladen. MOBIB-gebruikers kunnen Villo! tot eind september gratis uitproberen.<P></P>(JVH) ', 'Het Laatste Nieuws', NULL, NULL, '<p>Brussel</p><p>Fiets ontlenen met metrokaart</p><p>Wie in Brussel een fiets wil huren van fietsverhuursysteem Villo! kan dat vanaf nu ook met zijn metroabonnement any MOBIB-kaart. De Brusselse vervoersmaatschappij MIVB en Villo! hebben daarover een akkoord ondertekend.</p>', '2011-05-12 00:00:00', NULL, 'Fiets ontlenen met metrokaart', 'Lexisnexis', false, 0, NULL, NULL, 0);
