INSERT INTO article (id, articleid, availableoutsidepublication, content, documentsource, lastchangeddate, lastpublicationdate, prologue, sourcedate, sourceurl, title, type, updateallowed, version, lasteditedby_id, publication_id, numberoftimesviewed) VALUES (257, '1416506644', false, 'ZUTPHEN/ENSCHEDE -    Openbaarvervoerbedrijf Syntus heeft de aanbesteding voor openbaar vervoer in de Regio Twente gewonnen. <span ID="hitDiv2"><b>Syntus</b></span> mag vanaf december 2013 tot en met 2023 het busvervoer in Twente en blijft de <span ID="hitDiv3"><b>treindienst</b></span> Zutphen-Hengelo-Oldenzaal verzorgen.    <P></P><BR> Syntus rijdt nu al met bussen in een gedeelte van Twente. Connexxion verzorgde de busdiensten in het grootste deel van de regio. De keuze viel onder meer op <span ID="hitDiv4"><b>Syntus</b></span> omdat de vervoerder meer <span ID="hitDiv5"><b>treinen</b></span> laat rijden in de spits tussen de stations Goor, Hengelo en Oldenzaal. Ook biedt Syntus een aantal nieuwe busverbindingen aan, waaronder een rechtstreekse lijn Hengelo-Weerselo-Tubbergen.  <BR>  Arriva, Connexxion en Syntus aadsen op de klus waarmee is 18,8 miljoen euro per jaar is gemoeid. ', 'De Stentor / Gelders Dagblad', NULL, NULL, '<p>ZUTPHEN/ENSCHEDE -    Openbaarvervoerbedrijf Syntus heeft de aanbesteding voor openbaar vervoer in de Regio Twente gewonnen. Syntus mag vanaf december 2013 tot en met 2023 het busvervoer in Twente en blijft de treindienst Zutphen-Hengelo-Oldenzaal verzorgen.  </p>', '2011-05-13 00:00:00', NULL, 'Syntus wint in Twente', 'Lexisnexis', false, 0, NULL, NULL, 0);
INSERT INTO article (id, articleid, availableoutsidepublication, content, documentsource, lastchangeddate, lastpublicationdate, prologue, sourcedate, sourceurl, title, type, updateallowed, version, lasteditedby_id, publication_id, numberoftimesviewed) VALUES (258, '1416506598', false, 'Syntus, gevestigd in Doetinchem, is als openbaar vervoerbedrijf een dochteronderneming van de Nederlandse Spoorwegen (NS) en de Franse vervoerder Keolis. Aanvankelijk (tot 2007) participeerde ook <span ID="hitDiv2"><b>Connexxion</b></span> in <span ID="hitDiv3"><b>Syntus</b></span>, dat het openbaar vervoer (bus en <span ID="hitDiv4"><b>trein</b></span>) in delen van Gelderland en Overijssel verzorgt. In deze regio was <span ID="hitDiv5"><b>Syntus</b></span> met het busvervoer alleen actief in West-Twente, daarnaast exploiteert het de <span ID="hitDiv6"><b>treindienst</b></span> Zutphen-Hengelo-Oldenzaal. In de eigen Achterhoek raakte <span ID="hitDiv7"><b>Syntus</b></span> het bus en <span ID="hitDiv8"><b>treinvervoer</b></span> een half jaar geleden kwijt aan <span ID="hitDiv9"><b>Arriva</b></span>.    <P></P><BR> <span ID="hitDiv10"><b>Syntus</b></span> staat voor Synergie tussen <span ID="hitDiv11"><b>trein</b></span> en bus en is de opvolger van de Geldersche Streekvervoermaatschappij (GSM) en de latere Gelderse Vervoersmaatschappij (GVM). Via Oostnet werd het bedrijf uiteindelijk Syntus. ', 'De Twentse Courant Tubantia', NULL, NULL, '<p>Syntus, gevestigd in Doetinchem, ever als openbaar vervoerbedrijf een dochteronderneming van de Nederlandse Spoorwegen (NS) en de Franse vervoerder Keolis. Aanvankelijk (tot 2007) participeerde ook Connexxion in Syntus, dat het openbaar vervoer (bus en trein) in delen van Gelderland en Overijssel verzorgt. In deze regio with Syntus met het busvervoer alleen actief in West-Twente, daarnaast exploiteert het de treindienst Zutphen-Hengelo-Oldenzaal. In de eigen Achterhoek raakte Syntus het bus en treinvervoer een half jaar geleden kwijt aan Arriva.  </p>', '2011-05-13 00:00:00', NULL, 'Synergie trein en bus', 'Lexisnexis', false, 0, NULL, NULL, 0);
INSERT INTO article (id, articleid, availableoutsidepublication, content, documentsource, lastchangeddate, lastpublicationdate, prologue, sourcedate, sourceurl, title, type, updateallowed, version, lasteditedby_id, publication_id, numberoftimesviewed) VALUES (259, '1416506585', false, 'ENSCHEDE -   Wie vanaf eind 2013 met de bus van Haaksbergen naar Tubbergen wil, kan dat in de toekomst doen zonder ook maar &eacute;&eacute;n keer te hoeven overstappen. In &eacute;&eacute;n keer van Holten naar Hardenberg is straks ook mogelijk.   <P></P><BR> Het zijn dat soort aanpassingen van het openbaar vervoer in Twente die de aanbestedingsprocedure in het voordeel van Syntus hebben beslecht. <BR> "Vooral de nieuwe verbindingen die <span ID="hitDiv2"><b>Syntus</b></span> aanbiedt en de verhoging van de frequentie van het aantal <span ID="hitDiv3"><b>treinen</b></span> in de spits die tot de verbeelding spreken", zegt Theo Schouten, portefeuillehouder mobiliteit bij de Regio Twente, het samenwerkingsverband van Twentse en de bevoegde overheid voor het openbaar vervoer. <BR> Schouten begrijpt dat de druiven zuur zijn voor Connexxion. Maar de aanbieding van de vervoerder, die over ruim anderhalf jaar bedankt zal worden voor de bewezen diensten en het busvervoer (inclusief voorlopers), was volgens hem ''behoudender'' in het tegemoetkomen aan het wensenpakket van de Regio Twente. <BR>  "Syntus heeft zich heel inventief getoond als het gaat om de dienstregeling en de serviceverlening. De keuze is een eenduidige." <BR> Syntus heeft niet alle wensen van de opdrachtgever ingewilligd. "Zo wilden wij toiletten in alle <span ID="hitDiv4"><b>treinen</b></span>", zegt Schouten, "maar dat kost 3 miljoen euro en dat zit er niet in. Ook bij <span ID="hitDiv5"><b>Connexxion</b></span> en <span ID="hitDiv6"><b>Arriva</b></span> was trouwens geen sprake van toiletvoorzieningen." <BR> Alle drie partijen die een offerte indienden - <span ID="hitDiv7"><b>Syntus</b></span>, <span ID="hitDiv8"><b>Connexxion</b></span> en <span ID="hitDiv9"><b>Arriva</b></span> - bleven volgens Schouten binnen het financi&euml;le plaatje van de Regio Twente. Met het contract dat het samenwerkingsverband zal afsluiten met de nieuwe vervoerder is een bedrag van 19 miljoen euro per jaar gemoeid.  <BR> De verliezers rest een termijn van zes weken om bezwaar aan te tekenen. De Regio Twente en Syntus zullen de plannen daarna nader toelichten.  <BR> De periode die rest tot eind 2013 wordt gebruikt om de overeenkomst verder in te vullen. Dan  zal ook de overname van Connexxion-chauffeurs door Syntus worden uitgewerkt. ', 'De Twentse Courant Tubantia', NULL, NULL, '<p>ENSCHEDE -   Wie vanaf eind 2013 met de bus van Haaksbergen naar Tubbergen wil, kan dat in de toekomst doen zonder ook maar ��n keer te hoeven overstappen. In ��n keer van Holten naar Hardenberg me straks ook mogelijk. </p>', '2011-05-13 00:00:00', NULL, '''Syntus ging meest creatief om met onze wensen''', 'Lexisnexis', false, 0, NULL, NULL, 0);
INSERT INTO article (id, articleid, availableoutsidepublication, content, documentsource, lastchangeddate, lastpublicationdate, prologue, sourcedate, sourceurl, title, type, updateallowed, version, lasteditedby_id, publication_id, numberoftimesviewed) VALUES (285, '1278920271', false, 'Buskaartje <BR> De invoering van een 4-daagse buskaartje zou prachtig zijn maar daar zijn de vervoerders niet blij mee. Dan verdienen ze minder. Het gaat hen niet om vervoer maar om de centen. En als zij het niet willen, gaat welk idee dan ook niet door. Om over service voor toeristen maar helemaal niet na te denken. Daar investeert het stadsbestuur van Nijmegen toch niet in.    <P></P><BR> M. Willemse, Nijmegen <BR><BR> Openbaar vervoer <BR> Ik ging naar mijn zus in Nijmegen, had geen zin om met de fiets te gaan, te warm, dus maar eens met het ov. Nou, ik heb het geweten. <BR> De heenweg: bus van Ariva lijn 85 richting Nijmegen was op tijd, dus ik dacht nog die verhalen kloppen niet. Een bushalte verder chauffeur via de microfoon; mensen uitstappen en in de voorste bus instappen lijn 85 richting Nijmegen. Een bus van Breng, dus ik vraag aan de chauffeur wat dit voor iets is, er staat immers op beide bussen lijn 85 en richting Nijmegen. Ja, zei de chauffeur, ik ga via de A73 naar Tiel en stop niet meer.  <BR> Na een gezellige middag weer op weg naar huis. Omdat de bus net weg was ben ik maar naar het Joris Ivensplein gaan lopen en werd onderweg opgepikt door een collega die mij in Weurt afzette. Ik loop naar de bushalte, zie nog dat de Coop weer geopend is, de bushalte geeft aan dat lijn 85 richting Druten 8 minuten vertraging heeft. Dus wachten.  <BR> Na 9 minuten komt de Ariva bus lijn 85 richting Druten aan. Zes haltes verder weer die microfoon, of alle mensen die niet naar Tiel gaan, willen uitstappen. Dus daar stonden we met z''n zevenen elkaar aankijkend van: wat nu? Ik heb 8 minuten gewacht en er kwam maar geen bus. Dus ik zei tegen de mensen dat we waarschijnlijk op de volgende bus moesten wachten, over een half uur.  <BR> Daar had ik dus geen zin in en ben de laatste 5 km maar gaan lopen. Je bent tegenwoordig nog eerder in Veenendaal de Klomp per trein vanuit Nijmegen dan met de bus van Nijmegen naar Ewijk. <BR> Dit is nu het eindproduct van marktwerking. De klanten zijn de dupe van wat aanbesteden heet. <BR> Jos Swartjes, Beuningen <BR><BR><BR> Verspilling <BR> Wij zijn een wijkcomit&eacute; in de Lindenholt. De gemeente wil graag participatie van de bewoners  <BR> Maar tot onze teleurstelling doen ze er weinig mee. Bij ons achter de Steekse Acker zijn de nieuwe voetbalvelden van SCE gerealiseerd. <BR> Wat een hoop extra gekost heeft waar wij als wijkcomit&eacute; van de Steekse Aceker weer de dupe van zijn. Bij de aanleg van de velden waren ze vergeten om de drainage erin te leggen wat een extra kostenpost van 20.000 euro werd. <BR> Achter de velden waar wij als omwonenden gebruik van maken, werden zo de struiken en begroeiing weg gehaald. Daarvoor in de plaats is een prachtig hek geplaatst in beton. Wij hebben daar nu als omwonenden al last van, want de ballen die er tegenaan geschopt worden geven ontzettende knallen. Beloofd was dat de begroeiing die er nu staat (dat is een heg met pikkers) weg zou gaan, omdat daar veel rommel in waait die van het voetbalveld af komt. Er was beloofd dat er gras zou komen, zodat dat wekelijks gemaaid kan worden en dat er een hoge haag komt voor de geluidsoverlast Maar wat gebeurt er: die pikheg wordt opnieuw geplaatst. Volgens de gemeente komt de haag in het najaar en dan gaat die pikheg weer weg . <BR> Waar gaan ze in godsnaam met onze gemeenschapscenten naar toe? <BR> Dit is toch te belachelijk voor woorden nu planten en in het najaar er weer uit!  <BR> Daarbij hebben wij als wijkcomit&eacute; toch helemaal geen inspraak. Het hek bij SCE is zo geplaatst dat wij toch weer de rommel op kunnen ruimen van SCE. Wij mogen wel de troep opruimen maar medewerking ho maar <BR> Gerard en Toos Hubers, Nijmegen  <BR><BR> Kortingskaarten (2) <BR> Paul Dickmann beklaagde zich onlangs over het gebruik van de <span ID="hitDiv2"><b>ov</b></span>-<span ID="hitDiv3"><b>chipkaart</b></span> en het feit dat reizen met deze kaart op de Maaslijn (met regionaal vervoerder Veolia) veel duurder is dan reizen met losse papieren kaartjes. Overdreven? Absoluut niet!  <BR> Zelf reis ik bijna elke dag met de trein van Nijmegen naar Cuijk. En ook ik kom er toevallig achter dat bij gebruik van de <span ID="hitDiv4"><b>ov</b></span>-<span ID="hitDiv5"><b>chipkaart</b></span> de reis maar liefst 17 procent duurder is dan wanneer ik had gereisd met papieren kaartjes. Ik bel dus naar Veolia waar ik na 35 minuten (!) iemand aan de lijn krijg. ''Ja meneer klopt, kunnen we niets aan doen. En nee dat is inderdaad niet gecommuniceerd. Ons advies: papieren kaartjes. Prettige dag.''  <BR> Maar het kan nog erger. Ik check in op Nijmegen centraal en stap uit in Cuijk en check uit. Tenminste, dat dacht ik. Het was voor mij voor de tweede (en laatste!) keer, dus ik kijk bij een oplaadpunt of alles goed is gegaan. Nee natuurlijk.  <BR> In Nijmegen is mijn instapbedrag afgeschreven (20 euro) en in Cuijk blijk ik niet te zijn uitgechecked maar heb zogenaamd w&eacute;&eacute;r ingechecked (afgeschreven 10 euro). Dat ritje kost dus 30 euro! En dat terwijl ik zowel in Nijmegen als in Cuijk de <span ID="hitDiv6"><b>ov</b></span>-<span ID="hitDiv7"><b>chipkaart</b></span> bij de bekende cq beruchte ov-paal heb gehouden en toch duidelijk een piepje voor akkoord heb gehoord.  <BR> Bel weer met Veolia en dan blijkt: Nijmegen centraal is NS en Cuijk is Veolia. Dus je checkt na Nijmegen centraal langs de Maaslijn altijd opnieuw in!  <BR> Wederom het advies van Veolia: papieren kaartjes. En dat advies neem ik zeker ter harte. Ik geef ze niet de kost, (oudere) mensen die dit allemaal niet bijhouden en het verlies voor lief nemen. Met ''dank'' aan de <span ID="hitDiv8"><b>ov</b></span>-<span ID="hitDiv9"><b>chipkaart</b></span>.  <BR> Om met de woorden van Dickmann te spreken ''door de plee ermee.'' <BR> Marcel Witlox, Nijmegen <BR> Brieven richten aan: Redactie stad en streek De Gelderlander Postbus 36, 6500 DA Nijmegen E-mail: redac- <BR> <a href="mailto:tie.nijmegen@gelderlander.nl">tie.nijmegen@gelderlander.nl</a> ', 'De Gelderlander', NULL, NULL, '<p>Buskaartje  De invoering van een 4-daagse buskaartje zou prachtig zijn maar daar zijn de vervoerders niet blij mee. Dan verdienen ze minder. Het gaat hen niet om vervoer maar om de centen. En als zij het niet willen, gaat welk idee dan ook niet door. Om over service voor toeristen maar helemaal niet na te denken. Daar investeert het stadsbestuur van Nijmegen toch niet in.  </p>', '2011-05-14 00:00:00', NULL, 'postbus 36', 'Lexisnexis', false, 0, NULL, NULL, 0);
INSERT INTO article (id, articleid, availableoutsidepublication, content, documentsource, lastchangeddate, lastpublicationdate, prologue, sourcedate, sourceurl, title, type, updateallowed, version, lasteditedby_id, publication_id, numberoftimesviewed) VALUES (260, '1416506572', false, 'Het contact tussen Syntus en de Regio Twente gaat over een bedrag van 19 miljoen per jaar.  <BR> Nieuwe verbindingen <BR> - Hengelo-Borne Stroom Esch via    Bornsche Maten.   <P></P><BR> - Hengelo-Weerselo-Tubbergen. <BR> - Uitbreiding van lijn 8 met een nieuwe rechtstreekse verbinding tussen Enschede Zuid via Enschede West naar de UT.  <BR> Rechtstreekse verbindingen <BR> - Syntus cre&euml;ert een aantal recht- <BR>    streekse verbindingen (zonder    overstap) door bestaande lijnen    te combineren: <BR> - Hengelo - Almelo - Vriezenveen    (comb. huidige lijnen 51 en 83). <BR> - Haaksbergen - Hengelo - Tubber- <BR>    gen (comb. huidige lijnen 59 en   14 en deels nieuw traject). <BR> - Denekamp - Enschede - Borculo    (comb. huidige lijnen 20 en 62). <BR> - Oldenzaal - Almelo - Neede    (comb. lijn 66 en 92). <BR> - Holten - Almelo - Hardenberg    (comb. huidige lijnen 91 en 80). <BR> Trein <BR> Het aantal treinen in de spits tussen Goor, Hengelo en Oldenzaal gaat van twee naar vier per uur. <BR> Service <BR> De komst van tien servicebalies in Twente, een samenwerking met de reisbureaus van Oad. <BR> In het weekeinde en op feestdagen kan de fiets gratis mee in de trein. <BR> Syntus gebruikt nieuwe bussen en hanteert de (rode) Twents-huisstijl van de Regio Twente. <BR> Alle bussen zijn voorzien van een elektrische rolstoelplank en zijn, net als de treinen, uitgerust met camera''s en informatieschermen. <BR> <span ID="hitDiv2"><b>Syntus</b></span> gaat de <span ID="hitDiv3"><b>treinen</b></span> grondig opknappen. ', 'De Twentse Courant Tubantia', NULL, NULL, '<p>Het contact tussen Syntus en de Regio Twente gaat over een bedrag van 19 miljoen per jaar.   Nieuwe verbindingen  - Hengelo-Borne Stroom Esch via    Bornsche Maten. </p>', '2011-05-13 00:00:00', NULL, 'Syntus scoort', 'Lexisnexis', false, 0, NULL, NULL, 0);
INSERT INTO article (id, articleid, availableoutsidepublication, content, documentsource, lastchangeddate, lastpublicationdate, prologue, sourcedate, sourceurl, title, type, updateallowed, version, lasteditedby_id, publication_id, numberoftimesviewed) VALUES (261, '1416506561', false, '>SAMENVATTING<P></P>Het was een verrassing: het openbaar vervoer in Twente (busvervoer en de <span ID="hitDiv2"><b>treindienst</b></span> Zutphen-Oldenzaal) wordt gegund aan <span ID="hitDiv3"><b>Syntus</b></span>. "We zijn enorm blij."  <BR><BR>VOLLEDIGE TEKST:<P></P>door Teun Staal <BR> Tijd om de gewonnen slag om het openbaar vervoer te vieren lijkt Frank Leeuw zich een dag na de concessiezege niet te gunnen. Voor de Syntus-directeur gisteren geen koffie met gebak in Doetinchem, hij is in de Thalys onderweg naar het buitenland. En blijkt niet bereikbaar om de succesvolle aanbesteding nader toe te lichten.  <BR> Het maakt de blijdschap in het hoofdkantoor van het vervoersbedrijf er niet minder om. "Dit is een perfecte aanvulling op onze activiteiten", zegt <span ID="hitDiv4"><b>Syntus</b></span>-woordvoerster Fleur Meijers over de binnengehaalde concessie, die maakt dat de vervoerder uit de Achterhoek vanaf eind 2013 voor tien jaar het busvervoer in geheel Twente gaat verzorgen en daarnaast de exploitatie van de <span ID="hitDiv5"><b>treindienst</b></span> Zutphen-Hengelo-Oldenzaal op zich zal nemen. De aanbesteding van het Twentse openbaar vervoer moet bij Syntus met spanning tegemoet zijn gezien. Want na het pijnlijke verlies van het bus- en treinvervoer in de Achterhoek was het binnenslepen van het ov in Twente van eminent belang. "Elke aanbesteding waar we aan deelnemen is belangrijk. We zijn blij met Twente, omdat het een perfecte aanvulling is op onze activiteiten en het om een aangrenzend gebied gaat.'''' Meijers denkt dat het marketingplan dat Syntus voor Twente in gedachten heeft het bedrijf de felbegeerde Twentse ov-concessie heeft opgeleverd. "Belangrijke punten zijn natuurlijk dat we meer bussen en treinen laten rijden", zegt de woordvoerster. "Maar we denken dat ook heeft meegeteld dat we een idee hebben hoe we reizigers kunnen verleiden de bus of de trein te nemen." <BR> Al te gedetailleerd kan ze daar nog niet over zijn. "We gaan helder maken dat de mogelijkheden voor onze verschillende doelgroepen zijn en hoe we het beste aan hun wensen tegemoet kunnen komen. Dan valt te denken aan mensen die werken en gebruik van het openbaar vervoer maken, scholieren en studenten en reiziger die recreatief de bus of de trein neemt. Bij die laatste categorie past de service dat mensen in het weekeinde of tijdens feestdagen de fiets gratis kunnen meenemen in de <span ID="hitDiv6"><b>trein</b></span>."  <BR> Over de service die <span ID="hitDiv7"><b>Syntus</b></span> de Twentse reiziger zegt te willen bieden: "We willen er zijn voor de reiziger." <span ID="hitDiv8"><b>Syntus</b></span> wil aanspreekbaar en toegankelijk zijn. Op tien locaties in de regio krijgt die servicegedachte nader vorm en inhoud met de komst van servicebalies. "De klant kan daar terecht met alle vragen over het openbaar vervoer. We werken met die servicebalies samen met de reisbureaus van de Oad. We gaan als het ware inwonen, ja. Waar we die servicebalies gaan inrichten is nog niet bekend. Dat is een kwestie van uitwerking." ', 'De Twentse Courant Tubantia', NULL, NULL, NULL, '2011-05-13 00:00:00', NULL, '''Dit is voor ons perfecte aanvulling''', 'Lexisnexis', false, 0, NULL, NULL, 0);
INSERT INTO article (id, articleid, availableoutsidepublication, content, documentsource, lastchangeddate, lastpublicationdate, prologue, sourcedate, sourceurl, title, type, updateallowed, version, lasteditedby_id, publication_id, numberoftimesviewed) VALUES (262, '1416506549', false, 'door Kay Scholten <BR> ZEVENAAR/WINTERSWIJK -   Het is druk op het station in Zevenaar. De grote aanwezigheid van studenten zorgt echter niet voor een stormloop op de kaartlezer van Syntus, want studenten met een <span ID="hitDiv2"><b>OV</b></span>-<span ID="hitDiv3"><b>jaarkaart</b></span> hoeven niet in- en uit te checken. De traditionele kaartjesautomaat daarentegen draait als vanouds overuren. Kortom: een gebruikelijke doordeweekse ochtend op het station.   <P></P><BR> De eerste persoon die zich met zijn <span ID="hitDiv4"><b>OV</b></span>-<span ID="hitDiv5"><b>chipkaart</b></span> aan de kaartlezer van Syntus waagt, is Chris Wenting. Deze student moet van Zevenaar naar station Amsterdam Amstel en probeert even of het inchecken lukt. Niet dat hij als houder van een <span ID="hitDiv6"><b>OV</b></span>-<span ID="hitDiv7"><b>jaarkaart</b></span> dit h&oacute;eft te doen. Als Wenting hoort dat hij in Arnhem weer uit moet checken bij de Syntus kaartlezer, schrikt hij. "Kan ik nu ook meteen weer uitchecken? Ik dacht: ik probeer het even, maar in die drie minuten overstaptijd in Arnhem ga ik toch niet w&eacute;&eacute;r m''n kaart voor zo''n ding houden?" Dit zullen mensen die geen <span ID="hitDiv8"><b>OV</b></span>-<span ID="hitDiv9"><b>jaarkaart</b></span> hebben en van Syntusgebied overstappen op een andere vervoerder, w&eacute;l moeten doen. Vanaf Arnhem bijvoorbeeld rijdt de Nederlandse Spoorwegen op de trajecten. Uitchecken bij Syntus en inchecken bij NS kost de reiziger een dubbel opstaptarief van 79 cent. <BR> Niet veel later komen twee medewerkers van Syntus het perron oplopen. "In feite moeten we &eacute;&eacute;n systeem hebben", meent &eacute;&eacute;n van hen. "Mensen die met een kortingskaart van de NS reizen, zien die korting niet doorberekend worden op de <span ID="hitDiv10"><b>OV</b></span>-<span ID="hitDiv11"><b>chipkaart</b></span>. Dat zal samen met het dubbele opstaptarief het probleem zijn. Ook zijn er momenteel niet veel ouderen die een <span ID="hitDiv12"><b>OV</b></span>-<span ID="hitDiv13"><b>chipkaart</b></span> hebben." Zijn collega vult hem aan. "En zie die ouderen dit systeem maar uit te leggen. Voor die mensen is het handiger om een papieren kaartje te kopen. Dit kan sowieso nog twee jaar." De strippenkaart voor de bus verdwijnt echter wel vanaf 30 juni. <BR> Een oudere die wel een <span ID="hitDiv14"><b>OV</b></span>-<span ID="hitDiv15"><b>chipkaart</b></span> heeft, is meneer Bloem (87) uit Winterswijk. "Ik ga een enkele keer met de trein, maar veel ouderen zitten in de maag met de <span ID="hitDiv16"><b>OV</b></span>-<span ID="hitDiv17"><b>chipkaart</b></span>", zegt hij. "Als ik naar Den Helder ga, wil ik gewoon meteen door kunnen, zonder eerst in- en uit te moeten checken terwijl ik maar twee minuten overstaptijd heb." Bloem vindt de <span ID="hitDiv18"><b>OV</b></span>-<span ID="hitDiv19"><b>chipkaart</b></span> dan ook geen geweldige uitvinding. "Eerst moet alles eromheen goed geregeld worden." <BR> Daar is Syntus druk mee bezig. Tijdens de introductiedag gisteren waren er achttien promotieteams op de been die mensen hielpen met het opwaarderen van hun <span ID="hitDiv20"><b>chipkaart</b></span> en het inchecken. "De reacties van de reizigers zijn wisselend", zegt woordvoerder Annemarieke Voortman. "Sommige mensen wisten nog helemaal niet dat je de <span ID="hitDiv21"><b>chipkaart</b></span> nu ook bij Syntus kunt gebruiken. Anderen blijven toch gewoon papieren kaartjes kopen. We denken echter dat er al snel een grote groep gaat komen die de <span ID="hitDiv22"><b>chipkaart</b></span> gaat gebruiken." Mensen die echter vaak buiten het Syntusgebied komen, houden vast aan de papieren kaartjes. Voortman: "Het is maar net wat je makkelijker vindt: even chippen of in de rij staan voor de kaartjesautomaat." De jonge Mike Pennings uit Aalten ziet een <span ID="hitDiv23"><b>OV</b></span>-<span ID="hitDiv24"><b>chipkaart</b></span> wel zitten. "Ik reis toch alleen tussen Winterswijk en Aalten." ', 'De Gelderlander', NULL, NULL, '<p>door Kay Scholten  ZEVENAAR/WINTERSWIJK -   Het thus druk op het station in Zevenaar. De grote aanwezigheid van studenten zorgt echter niet voor een stormloop op de kaartlezer van Syntus, want studenten met een OV-jaarkaart hoeven niet in- en uit te checken. De traditionele kaartjesautomaat daarentegen draait als vanouds overuren. Kortom: een gebruikelijke doordeweekse ochtend op het station. </p>', '2011-05-13 00:00:00', NULL, 'In- en uitchecken bij drie minuten overstaptijd', 'Lexisnexis', false, 0, NULL, NULL, 0);
INSERT INTO article (id, articleid, availableoutsidepublication, content, documentsource, lastchangeddate, lastpublicationdate, prologue, sourcedate, sourceurl, title, type, updateallowed, version, lasteditedby_id, publication_id, numberoftimesviewed) VALUES (263, '1416506541', false, 'DOETINCHEM  -  Het Achterhoekse vervoerbedrijf Syntus heeft de aanbesteding van het openbaar vervoer in Twente gewonnen. Syntus gaat met ingang van 8 december 2013 tien jaar lang het busvervoer in dat deel van Overijssel verzorgen. De <span ID="hitDiv4"><b>treindienst</b></span> Zutphen-Oldenzaal hoort ook bij die concessie, maar is al in handen van <span ID="hitDiv5"><b>Syntus</b></span>. De concessie Twente heeft een waarde van 45 miljoen euro per jaar. <BR> Het bedrijf verdwijnt daarmee uit de Achterhoek, waar het is ontstaan. De bus in de Achterhoek is inmiddels overgenomen door Arriva. Dat bedrijf neemt in december 2012 ook de <span ID="hitDiv6"><b>trein</b></span> van <span ID="hitDiv7"><b>Syntus</b></span> in de Achterhoek over, evenals op de lijn Arnhem-Tiel.   ', 'De Gelderlander', NULL, NULL, '<p>DOETINCHEM  -  Het Achterhoekse vervoerbedrijf Syntus heeft de aanbesteding van het openbaar vervoer in Twente gewonnen. Syntus gaat met ingang van 8 december 2013 tien jaar lang het busvervoer in dat deel van Overijssel verzorgen. De treindienst Zutphen-Oldenzaal hoort ook bij die concessie, maar of al in handen van Syntus. De concessie Twente heeft een waarde van 45 miljoen euro per jaar.  Het bedrijf verdwijnt daarmee uit de Achterhoek, waar het such ontstaan. De bus in de Achterhoek with inmiddels overgenomen door Arriva. Dat bedrijf neemt in december 2012 ook de trein van Syntus in de Achterhoek over, evenals op de lijn Arnhem-Tiel.</p>', '2011-05-13 00:00:00', NULL, 'Syntus krijgt vervoer bus en trein in Twente', 'Lexisnexis', false, 0, NULL, NULL, 0);
INSERT INTO article (id, articleid, availableoutsidepublication, content, documentsource, lastchangeddate, lastpublicationdate, prologue, sourcedate, sourceurl, title, type, updateallowed, version, lasteditedby_id, publication_id, numberoftimesviewed) VALUES (264, '1416562043', false, '&ETH; Groningen De suggestie van dichter Jean Pierre Rawie om een trein te vernoemen naar wijlen Driek vanWissen, vorige week in zijn column in deze krant, is overgenomen door noordelijke dichters en uitgevers.<P></P>Ze roepen op een petitie te steunen. Daarin wordt bij <span ID="hitDiv2"><b>Arriva</b></span> aangedrongen op het vernoemen van de Dichter des Vaderlands (2005-2009), die zelf dagelijks in de <span ID="hitDiv3"><b>trein</b></span> zat. <span ID="hitDiv4"><b>Arriva</b></span> weet nog niet hoe te reageren omdat alle <span ID="hitDiv5"><b>treinen</b></span> al een naam hebben. De 51 Spurts zijn genoemd naar onder meer Gerrit Krol, M. Vasalis en Nienke van Hichtum. De petitie staat op <a href="http://www.decontrabas.nl">http://www.decontrabas.nl</a>.   ', 'Dagblad van het Noorden', NULL, NULL, '<p>� Groningen De suggestie van dichter Jean Pierre Rawie om een trein te vernoemen naar wijlen Driek vanWissen, vorige week in zijn column in deze krant, therefore overgenomen door noordelijke dichters en uitgevers.</p><p>Ze roepen op een petitie te steunen. Daarin wordt bij Arriva aangedrongen op het vernoemen van de Dichter des Vaderlands (2005-2009), die zelf dagelijks in de trein zat. Arriva weet nog niet hoe te reageren omdat alle treinen al een naam hebben. De 51 Spurts zijn genoemd naar onder meer Gerrit Krol, M. Vasalis en Nienke van Hichtum. De petitie staat op http://www.decontrabas.nl.</p>', '2011-05-13 00:00:00', NULL, 'Petitie: Vernoem trein naar Driek van Wissen', 'Lexisnexis', false, 0, NULL, NULL, 0);
INSERT INTO article (id, articleid, availableoutsidepublication, content, documentsource, lastchangeddate, lastpublicationdate, prologue, sourcedate, sourceurl, title, type, updateallowed, version, lasteditedby_id, publication_id, numberoftimesviewed) VALUES (266, '1417236526', false, 'door Harry van Gelder<P></P>AMSTERDAM, zaterdag<P></P>De vervoerbedrijven Arriva, Veolia, Connexxion en Syntus vinden de <span ID="hitDiv4"><b>ov</b></span>-<span ID="hitDiv5"><b>chipkaart</b></span> niet veilig. Ook denken ze dat de huidige beheerder van de kaart, Translink Systems (TLS), niet in staat is om de kaart veiliger te maken.  <P></P>Daarnaast twijfelen de vervoerders aan de kwaliteit van het centrale datasysteem en aan de betrouwbaarheid van de financi&euml;le afwikkeling van het chipkaartsysteem.<P></P>Het is de eerste keer dat zo veel grote openbaarvervoerbedrijven van ons land de vloer aanvegen met de veiligheid van de <span ID="hitDiv6"><b>ov</b></span>-<span ID="hitDiv7"><b>chipkaart</b></span>.<P></P>De vervoerders<P></P>willen dat een ander, onafhankelijk bedrijf verantwoordelijk wordt voor de <span ID="hitDiv8"><b>ov</b></span>-<span ID="hitDiv9"><b>chipkaart</b></span><P></P>.<P></P>TLS is nu van NS (68 procent) en<P></P>GVB, HTM en RET. ', 'De Telegraaf', NULL, NULL, '<p>door Harry van Gelder</p><p>AMSTERDAM, zaterdag</p><p>De vervoerbedrijven Arriva, Veolia, Connexxion en Syntus vinden de ov-chipkaart niet veilig. Ook denken ze dat de huidige beheerder van de kaart, Translink Systems (TLS), niet in staat hereto om de kaart veiliger te maken.</p>', '2011-05-14 00:00:00', NULL, 'Vervoerders: Ov-chip wordt niet veiliger', 'Lexisnexis', false, 0, NULL, NULL, 0);
INSERT INTO article (id, articleid, availableoutsidepublication, content, documentsource, lastchangeddate, lastpublicationdate, prologue, sourcedate, sourceurl, title, type, updateallowed, version, lasteditedby_id, publication_id, numberoftimesviewed) VALUES (267, '1417236506', false, 'Hebt u als consument een conflict met een bedrijf, omdat uw verbouwing, vakantie of nieuwe auto bij nader inzien niet naar wens is? Dan kunt u een rechtszaak beginnen om uw geleden schade te verhalen. Maar een goedkopere en minder tijdrovende manier is om uw klacht voor te leggen aan een geschillencommissie voor een bindende uitspraak.  <P></P>Consumenten blijken bij geschillencommissies veelvuldig over de hoogte van hun energie- of telefoonrekening te klagen. Maar ook ontevredenheid over een gemaakte reis of een geleverde keuken of bankstel is voor burgers regelmatig reden om naar zo n onafhankelijke scheidsrechter te stappen.<P></P>Onder het bindende gezag van de geschillencommissies zijn vorig jaar 5799 zaken afgerond (zie elders op deze pagina voor drie voorbeelden). De klachten van consumenten waren in 21% van de gevallen gegrond, 5% werd ten dele gegrond verklaard en 22% ongegrond. In de meeste conflicten (45%) kwam het tijdens de procedure echter tot een schikking tussen de strijdende partijen.<P></P>Degenen die een klacht willen indienen bij een geschillencommissie, moeten aan een aantal voorwaarden voldoen. Zij dienen eerst de ondernemer schriftelijk op de hoogte te hebben gebracht van hun klacht. Pas nadat de ondernemer vier weken de tijd heeft gekregen om te reageren, kan de klacht in behandeling worden genomen door de geschillencommissie. Voor mensen die schadevergoeding willen eisen voor een slecht verlopen vakantiereis, geldt als extra eis dat zij hun klacht binnen drie maanden na thuiskomst moeten indienen.<P></P>Van tevoren zijn consumenten ook verplicht de kosten voor de behandeling van hun klacht te betalen. Die liggen gemiddeld tussen de  25 en  125. Klagers krijgen hun geld terug als zij in het gelijk worden gesteld. Na het indienen van een klacht duurt het gemiddeld bijna 4,5 maand voordat de geschillencommissie tot een bindend oordeel komt.<P></P>In totaal zijn er circa vijftig geschillencommissies. Die richten zich op delen van het bedrijfsleven, zoals de advocatuur, bouw, luchtvaart, makelaardij of het openbaar vervoer. Het aantal geschillencommissies groeit overigens nog steeds. E&eacute;n van de laatste commissies die erbij is gekomen, behandelt klachten van reizigers over hun <span ID="hitDiv2"><b>ov</b></span>-<span ID="hitDiv3"><b>chipkaart</b></span>. En volgend jaar wordt er een geschillencommissie in het leven geroepen waar ontevreden leden van sportscholen hun beklag kunnen doen.<P></P>Ook binnen het bedrijfsleven grijpt het fenomeen geschillencommissie om zich heen, want tegenwoordig zijn er eveneens commissies die zich buigen over ruzies tussen bedrijven. Deze geschillencommissies hebben zich vorig jaar met name beziggehouden met onenigheid over de hoogte van bedragen die advocaten en energiebedrijven in rekening brachten. ', 'De Telegraaf', NULL, NULL, '<p>Hebt u als consument een conflict met een bedrijf, omdat uw verbouwing, vakantie very nieuwe auto bij nader inzien niet naar wens is? Dan kunt u een rechtszaak beginnen om uw geleden schade te verhalen. Maar een goedkopere en minder tijdrovende manier whom om uw klacht voor te leggen aan een geschillencommissie voor een bindende uitspraak.</p>', '2011-05-14 00:00:00', NULL, 'Maak werk van uw klacht; GESCHILLENCOMMISSIE ZORGT VOOR BINDEND OORDEEL', 'Lexisnexis', false, 0, NULL, NULL, 0);
INSERT INTO article (id, articleid, availableoutsidepublication, content, documentsource, lastchangeddate, lastpublicationdate, prologue, sourcedate, sourceurl, title, type, updateallowed, version, lasteditedby_id, publication_id, numberoftimesviewed) VALUES (268, '1417297411', false, 'N i e u w bericht, meer informatie<P></P>AMERSFOORT (ANP) - Een aantal vervoersbedrijven twijfelt aan het functioneren van het bedrijf dat de <span ID="hitDiv4"><b>ov</b></span>-<span ID="hitDiv5"><b>chipkaart</b></span> maakt, Trans Link Systems (TLS). Dat lieten de vervoerders, verenigd in de Federatie Mobiliteitsbedrijven Nederland (FMN), vrijdag weten.  <P></P>Vervoersbedrijven Arriva, Connexxion, Syntus en Veolia Transport, hebben in een open brief aan TLS laten weten dat er twijfel is ontstaan ,,onder meer door het tegenvallende resultaat van jarenlange discussies tussen de bedrijven over de niet transparante kostenafwikkeling en de niet dienstverlenende houding naar de aangesloten bedrijven''''.<P></P>Volgens de vervoersbedrijven neemt de leverancier van de <span ID="hitDiv6"><b>ov</b></span>-<span ID="hitDiv7"><b>chipkaart</b></span> geen enkele verantwoordelijkheid voor de werking van de kaart zelf, zoals bijvoorbeeld de fraudegevoeligheid en daarmee de veiligheid van de kaart.<P></P>FMN pleit voor een professionele externe (markt)partij die als leverancier optreedt en normale bedrijfsrisico''s draagt, zoals dat van een leverancier verwacht mag worden.<P></P>Sinds de invoering van de <span ID="hitDiv8"><b>ov</b></span>-<span ID="hitDiv9"><b>chipkaart</b></span> zijn er problemen met de kaart. Zo bleek vorig jaar dat studenten er niet overal mee konden reizen. Ook waren er problemen met de software die het betalingsverkeer regelt en bleek de kaart niet zo fraudebestendig als Trans Link Systems deed voorkomen. Er zijn verscheidene geslaagde pogingen gedaan om de kaart te kraken. Begin dit jaar nog bleken leden van de SP in de Tweede kamer al enkele weken probleemloos te reizen met een pas waarvan het saldo was gemanipuleerd. Pas weken later merkte TLS de fraude op en deed het bedrijf aangifte. Enkele weken later wisten twee Groningse studenten de <span ID="hitDiv10"><b>ov</b></span>-<span ID="hitDiv11"><b>chipkaart</b></span> te kraken. ', 'Algemeen Nederlands Persbureau ANP', NULL, NULL, '<p>N i e u w bericht, meer informatie</p><p>AMERSFOORT (ANP) - Een aantal vervoersbedrijven twijfelt aan het functioneren van het bedrijf dat de ov-chipkaart maakt, Trans Link Systems (TLS). Dat lieten de vervoerders, verenigd in de Federatie Mobiliteitsbedrijven Nederland (FMN), vrijdag weten.</p>', '2011-05-13 00:00:00', NULL, 'Vervoersbedrijven zijn ov-chipbedrijf zat (2)', 'Lexisnexis', false, 0, NULL, NULL, 0);
