INSERT INTO article (id, articleid, availableoutsidepublication, content, documentsource, lastchangeddate, lastpublicationdate, prologue, sourcedate, sourceurl, title, type, updateallowed, version, lasteditedby_id, publication_id, numberoftimesviewed) VALUES (287, '1417601430', false, 'AMERSFOORT Een aantal vervoersbedrijven twijfelt aan het bedrijf dat de <span ID="hitDiv2"><b>ov</b></span>-<span ID="hitDiv3"><b>chipkaart</b></span> maakt, Trans Link Systems (TLS). Dat liet hun Federatie Mobiliteitsbedrijven Nederland (FMN) gisteren weten.  <P></P>Arriva, Connexxion, Syntus en Veolia Transport hebben in een open brief aan TLS laten weten dat er twijfel is ontstaan ''onder meer door het tegenvallende resultaat van jarenlange discussies tussen de bedrijven over de niet transparante kostenafwikkeling en de niet dienstverlenende houding naar de aangesloten bedrijven.''<P></P>Volgens de brief neemt de leverancier van de <span ID="hitDiv4"><b>ov</b></span>-<span ID="hitDiv5"><b>chipkaart</b></span> geen enkele verantwoordelijkheid voor de werking van de kaart zelf, zoals de fraudegevoeligheid en daarmee de veiligheid van de kaart.<P></P>FMN pleit voor een externe partij die als leverancier optreedt en normale bedrijfsrisico''s draagt.<P></P>Sinds de invoering van de <span ID="hitDiv6"><b>ov</b></span>-<span ID="hitDiv7"><b>chipkaart</b></span> geeft die problemen. Zo bleek dat studenten er niet overal mee konden reizen. Ook waren er problemen met de software die het betalingsverkeer regelt en bleek de kaart niet zo fraudebestendig als Trans Link Systems deed voorkomen. (ANP) ', 'Het Parool', NULL, NULL, '<p>AMERSFOORT Een aantal vervoersbedrijven twijfelt aan het bedrijf dat de ov-chipkaart maakt, Trans Link Systems (TLS). Dat liet hun Federatie Mobiliteitsbedrijven Nederland (FMN) gisteren weten.</p>', '2011-05-14 00:00:00', NULL, 'Ov boos op bedrijf achter chip', 'Lexisnexis', false, 0, NULL, NULL, 0);
INSERT INTO article (id, articleid, availableoutsidepublication, content, documentsource, lastchangeddate, lastpublicationdate, prologue, sourcedate, sourceurl, title, type, updateallowed, version, lasteditedby_id, publication_id, numberoftimesviewed) VALUES (279, '1395555049', false, '>SAMENVATTING<P></P>Brieven, van maximaal 150 woorden, aan: redactie. doetinchem@ <a href="http://gelderlander.nl">http://gelderlander.nl</a>. De redactie behoudt zich het recht voor brieven in te korten of te weigeren.  <BR><BR>VOLLEDIGE TEKST:<P></P>Huntenkunst <BR> (''Huntenkunst past in DRU, 6 mei) <BR> Als beeldend kunstenaar heb ik vanaf de eerste keer in mijn woonplaats Veldhunten tot en met de eerste keer in sporthal Zuid meegedaan met Huntenkunst. Maar wat een teleurstelling was de laatste locatie vergeleken met de Markhal in Doetinchem. Ik kon er mijn auto niet kwijt, er was geen mooi daklicht, en dan die zwarte ''plastic'' vloerbedekking en die beperkte ruimte. Ik ben toen afgehaakt na zestien jaar Huntenkunst. <BR> Met Harrie Schenning heb ik het er vaak over gehad dat het beter zou zijn om naar DRU-terrein in Ulft te verhuizen. Ik weet ook wel dat die Doetinchemse subsidie belangrijk is, maar het gaat wel om de kwaliteit, niet alleen van de kunst, maar ook van de plek. Een voordeel is dat er buiten de hal genoeg plek is die gebruikt kan worden voor andere kunstuitingen.  <BR> Huntenkunst kan en moet gewoon bij de DRU Cultuurfabriek. Ik kan namens Kunstenaars Collectief Breekijzer zeggen dat wij er volledig achter staan als Huntenkunst naar Ulft komt. <BR> Frans Venhorst  <BR> beeldend kunstenaar, Varsselder-Veldhunten  <BR><BR><BR> Toegangssluis <BR> Met steeds krommer wordende tenen, volg ik van begin af aan de discussie over het Domus-huis in Wehl. Een steeds navranter duidelijk wordende xenofobie in de discussie, doet mijn maag steeds verder omkeren. Als klap op de vuurpijl bezigt blijkbaar burgemeester Kaiser van Doetinchem de besmette omschrijving ''toegangssluis''. De primus inter pares van de Achterhoek lijkt hiermee nadrukkelijk stelling te betrekken in deze toch al moeizaam verlopende discussie. Wat is dat anders dan olie op het vuur van xenofobie gooien?  <BR> J. Apers <BR> Laag Keppel <BR><BR><BR> <span ID="hitDiv2"><b>OV</b></span>-<span ID="hitDiv3"><b>chipkaart</b></span> <BR> "Zie aan die ouderen dit systeem maar eens uit te leggen", aldus een spoormedewerker in een vraaggesprekje over de invoering van de <span ID="hitDiv4"><b>OV</b></span>-<span ID="hitDiv5"><b>chipkaart</b></span> bij Syntus (De Gelderlander, 13 mei).  <BR> Toen ik dat las brak me de klomp. Tweemaal heb ik een voorlichtingsbijeenkomst over de <span ID="hitDiv6"><b>OV</b></span>-<span ID="hitDiv7"><b>chipkaart</b></span> bezocht. Beide keren hadden de toehoorders meer vragen dan de deskundigen konden beantwoorden.  <BR> De spoormedewerker had dus beter kunnen zeggen: "Probeer zo''n stom systeem als dat wat wij hebben bedacht maar eens aan het publiek uit te leggen."  <BR> Antoon Driessen <BR> Lichtenvoorde ', 'De Gelderlander', NULL, NULL, NULL, '2011-05-14 00:00:00', NULL, '@redactie', 'Lexisnexis', false, 0, NULL, NULL, 0);
