INSERT INTO article (id, articleid, availableoutsidepublication, content, documentsource, lastchangeddate, lastpublicationdate, prologue, sourcedate, sourceurl, title, type, updateallowed, version, lasteditedby_id, publication_id, numberoftimesviewed) VALUES (289, '1417916542', false, 'OLDENZAAL -   In het stadhuis in Oldenzaal vindt morgen van 14.00 tot 17.00 uur een servicebijeenkomst over de <span ID="hitDiv4"><b>ov</b></span>-<span ID="hitDiv5"><b>chipkaart</b></span> plaats. Tijdens deze bijeenkomst kunnen reizigers kennismaken met deze kaart.   <P></P><BR> In het openbaar vervoer in Twente kan al enige tijd worden gereisd met de <span ID="hitDiv6"><b>ov</b></span>-<span ID="hitDiv7"><b>chipkaart</b></span>, maar ook nog altijd met de nationale strippenkaart. In de loop van dit jaar zal waarschijnlijk ook in Oldenzaal en omgeving de strippenkaart echter worden afgeschaft als vervoerbewijs. Dat betekent dat er dan uitsluitend met de <span ID="hitDiv8"><b>ov</b></span>-<span ID="hitDiv9"><b>chipkaart</b></span> kan worden gereisd. <BR> De openbaarvervoerbedrijven hebben een campagne opgezet om reizigers vertrouwd te maken met dit nieuwe systeem. Bij de introductie van de <span ID="hitDiv10"><b>ov</b></span>-<span ID="hitDiv11"><b>chipkaart</b></span> in de Randstad ondervonden veel reizigers nogal wat startproblemen met de card. <BR> Een onderdeel van de campagne is een <span ID="hitDiv12"><b>ov</b></span>-<span ID="hitDiv13"><b>chipkaart</b></span> servicebijeenkomst. Daarin wordt uitgelegd hoe het reizen met de <span ID="hitDiv14"><b>ov</b></span>-<span ID="hitDiv15"><b>chipkaart</b></span> werkt en welke soorten <span ID="hitDiv16"><b>ov</b></span>-<span ID="hitDiv17"><b>chipkaarten</b></span> er zijn. Namens het college zal wethouder verkeer en vervoer Frits Rorink de bezoekers welkom heten.  Na de uitvoerige uitleg krijgen bezoekers de gelegenheid zelf te oefenen met de nieuwe apparatuur. Verder is er hulp voor mensen die een kaart willen aanvragen.  <BR><BR> Aanmelden voor de bijeenkomst is noodzakelijk. Dat kan via de website <a href="http://www.ervaarhetov.nl">www.ervaarhetov.nl</a>, bij  <BR> alle acties en servicebijeenkomsten of via het telefoonnummer van de gemeente Oldenzaal: 0541-588111. <BR> "Het aantal beschikbare plaatsen is beperkt", zegt communicatiemedewerker Beate Nieuwhuis van de gemeente Oldenzaal . "Dus mensen moeten niet te lang wachten met aanmelden." ', 'De Twentse Courant Tubantia', NULL, NULL, '<p>OLDENZAAL -   In het stadhuis in Oldenzaal vindt morgen van 14.00 tot 17.00 uur een servicebijeenkomst over de ov-chipkaart plaats. Tijdens deze bijeenkomst kunnen reizigers kennismaken met deze kaart. </p>', '2011-05-16 00:00:00', NULL, 'Oefenen met de ov-chipkaart in het stadhuis van Oldenzaal', 'Lexisnexis', false, 0, NULL, NULL, 0);
