INSERT INTO article (id, articleid, availableoutsidepublication, content, documentsource, lastchangeddate, lastpublicationdate, prologue, sourcedate, sourceurl, title, type, updateallowed, version, lasteditedby_id, publication_id, numberoftimesviewed) VALUES (291, '1418559668', false, 'Dichters en uitgevers willen dat <span ID="hitDiv2"><b>Arriva</b></span> een <span ID="hitDiv3"><b>trein</b></span> noemt naar wijlen Driek van Wissen. Ze roepen daartoe op een petitie te ondertekenen.<P></P>Deze is te vinden op de website <a href="http://www.decontrabas.com">www.decontrabas.com</a>.<P></P>Het websiteadres dat we gisteren in deze krant publiceerden (<a href="http://www.decontrabas.nl">www.decontrabas.nl</a>) is onjuist.   ', 'Dagblad van het Noorden', NULL, NULL, '<p>Dichters en uitgevers willen dat Arriva een trein noemt naar wijlen Driek van Wissen. Ze roepen daartoe op een petitie te ondertekenen.</p><p>Deze therefrom te vinden op de website www.decontrabas.com.</p><p>Het websiteadres dat of gisteren in deze krant publiceerden (www.decontrabas.nl) does onjuist.</p>', '2011-05-14 00:00:00', NULL, 'HERSTEL', 'Lexisnexis', false, 0, NULL, NULL, 0);
INSERT INTO article (id, articleid, availableoutsidepublication, content, documentsource, lastchangeddate, lastpublicationdate, prologue, sourcedate, sourceurl, title, type, updateallowed, version, lasteditedby_id, publication_id, numberoftimesviewed) VALUES (292, '1418559637', false, ' Dirk Stelder <P></P>Na een bezoek aan de Vrije Universiteit stond ik op Amsterdam- Zuid op de trein terug te wachten. Het bleek tot mijn verrassing een Deutsche Bahn te zijn met sjieke coup&eacute;''s die op weg was naar Osnabr&uuml;ck en mij tot Amersfoort mee zou nemen.  <P></P>In de restauratiewagen kon ik diverse warme maaltijden bestellen en ging voor de chili con carne met vers getapt biertje voor 7,10 euro. De ober kwam het bierglas op pootje persoonlijk naar mijn tafeltje brengen en het eten was uitstekend.<P></P>Elke Groninger die regelmatig voor zijn werk in de Randstad moet zijn kent het probleem. Ergens tussen vijf en zes uur ga je op weg terug en zit met een lege maag in een trein waar je drie uur lang nog geen glaasje water kunt krijgen. Tenzij je vooraf alle proviand hebt ingeslagen die op de kleinere stations varieert tussen een vieze hamburger en helemaal niets.<P></P>De economische conclusie voor het spoor moet zijn dat de privatisering nog niet ver genoeg is gegaan. Laat de Deutsche Bahn meedingen naar de concessies op het Nederlandse spoor en binnen een jaar rijden er in heel Nederland alleen nog maar Duitse treinen met obers.<P></P>Hoe loopt het dan af met de stoptreinen? Ik meen gelezen te hebben dat de <span ID="hitDiv2"><b>Arriva</b></span> <span ID="hitDiv3"><b>trein</b></span> naar Nieuweschans soms zelfs niet eens meer een conducteur aan boord heeft, laat staan enige andere vorm van service. Nog erger zijn natuurlijk de nieuwe sprinters die niet eens meer een toilet hebben. Zo niet bij onze oosterburen.<P></P>Ik kan uit eigen ervaring melden dat in de Ik kan uit eigen ervaring melden dat in de Regionalexpress, de stoptrein dus, van Berlijn naar Greifswald ook op zondagavond nog altijd een automaat te vinden is met verse broodjes en dranken.<P></P>Ik hoop in de toekomst nog vaker een Duitstalig bonnetje te mogen declareren van een binnenlandse reis. ', 'Dagblad van het Noorden', NULL, NULL, '<p> Dirk Stelder </p><p>Na een bezoek aan de Vrije Universiteit stond ik op Amsterdam- Zuid op de trein terug te wachten. Het bleek tot mijn verrassing een Deutsche Bahn te zijn met sjieke coup�''s die op weg were naar Osnabr�ck en mij tot Amersfoort mee zou nemen.</p>', '2011-05-14 00:00:00', NULL, 'Binnenlandse reis', 'Lexisnexis', false, 0, NULL, NULL, 0);
INSERT INTO article (id, articleid, availableoutsidepublication, content, documentsource, lastchangeddate, lastpublicationdate, prologue, sourcedate, sourceurl, title, type, updateallowed, version, lasteditedby_id, publication_id, numberoftimesviewed) VALUES (293, '1417297325', false, 'OPEN BRIEF<P></P>AMERSFOORT - Een aantal vervoersbedrijven twijfelt aan het functioneren van het bedrijf dat de <span ID="hitDiv4"><b>ov</b></span>-<span ID="hitDiv5"><b>chipkaart</b></span> maakt, Trans Link Systems (TLS). Dat lieten de vervoerders, verenigd in de Federatie Mobiliteitsbedrijven Nederland (FMN), gisteren weten.<P></P>Vervoersbedrijven Arriva, Connexxion, Syntus en Veolia Transport, hebben in een open brief aan TLS laten weten dat er twijfel is ontstaan  onder meer door het tegenvallende resultaat van jarenlange discussies tussen de bedrijven over de niet transparante kostenafwikkeling en de niet dienstverlenende houding naar de aangesloten bedrijven". Volgens de vervoersbedrijven neemt de leverancier van de <span ID="hitDiv6"><b>ov</b></span>-<span ID="hitDiv7"><b>chipkaart</b></span> geen enkele verantwoordelijkheid voor de werking van de kaart zelf.   ', 'Limburgs Dagblad', NULL, NULL, '<p>OPEN BRIEF</p><p>AMERSFOORT - Een aantal vervoersbedrijven twijfelt aan het functioneren van het bedrijf dat de ov-chipkaart maakt, Trans Link Systems (TLS). Dat lieten de vervoerders, verenigd in de Federatie Mobiliteitsbedrijven Nederland (FMN), gisteren weten.</p><p>Vervoersbedrijven Arriva, Connexxion, Syntus en Veolia Transport, hebben in een open brief aan TLS laten weten dat er twijfel hereby ontstaan  onder meer door het tegenvallende resultaat van jarenlange discussies tussen de bedrijven over de niet transparante kostenafwikkeling en de niet dienstverlenende houding naar de aangesloten bedrijven". Volgens de vervoersbedrijven neemt de leverancier van de ov-chipkaart geen enkele verantwoordelijkheid voor de werking van de kaart zelf.</p>', '2011-05-14 00:00:00', NULL, 'Vervoerders zijn maker van ov-chipkaart zat', 'Lexisnexis', false, 0, NULL, NULL, 0);
INSERT INTO article (id, articleid, availableoutsidepublication, content, documentsource, lastchangeddate, lastpublicationdate, prologue, sourcedate, sourceurl, title, type, updateallowed, version, lasteditedby_id, publication_id, numberoftimesviewed) VALUES (294, '1418600564', false, 'Wat zal er een homerisch gelach opklinken uit Griekenland als de miljarden toestromen en de salarissen van de regeringsleiders omhoog kunnen. In Portugal blijven de miljarden aan goud lekker in de kluis. Als Nederland gaat steunen, krijgen wij dan tien euro per jaar korting op onze zorgpremie? Zou leuk zijn voor aow ers. Persoonlijk zal ik dan in welwillende overweging nemen bij volgende verkiezingen ook weer eens te gaan stemmen.<P></P>F. den Boer, Bovenkarspel  <P></P>Sponsorloop<P></P>Kan het Europees Parlement niet beter een sponsorloop organiseren van Brussel tot Athene? Om geld te genereren voor de Grieken? Tevens een goede training voor een volgende run om landen te helpen die in de rode cijfers belanden. Zo doen wij dat toch ook, met alle noodlijdende verenigingen waar onze kinderen lid van zijn?<P></P>Wilma Beskers-ten Barge, Borculo<P></P>Servetten<P></P>Of wij Griekenland nu te hulp schieten of niet, het gaat ons miljarden kosten en daar gaan wij als belastingbetaler voor opdraaien. Ruim voor de invoering van de euro in 2002 heb ik<P></P>al gezegd dat de gezamenlijke munt alleen ten goede zou komen aan de zwakkere broeders. Net als bij die barbecues, waar de een karbonaadjes meeneemt, de tweede sateetjes, de derde zorgt voor de wijn en dan is er altijd de laatste die de servetten meebrengt <P></P>Cees J. Roos, Sneek<P></P>Misdaden<P></P>De verwarde zwerver die een waxinelichtje gooide naar de koets van de Koningin, nu ruim een jaar geleden, zit al die tijd al vast, wordt bewaakt als de juwelen van Engeland en loopt kans op tbs. Hoe anders was het in 1947. De oorlogsmisdadiger Klaas-Carel Faber krijgt in Nederland levenslang voor kampmoorden op onschuldige Nederlanders. De bewaking is ver beneden peil,<P></P>want Faber loopt in 1950 de gevangenis uit en leeft zestig jaar lang onbezorgd in Duitsland. Thans staat hij terecht<P></P>voor zijn inmiddels bewezen wandaden. Maar Duitsland levert hem niet uit, Faber blijft heerlijk genieten. Datzelfde Duitsland doet elk jaar verwoede pogingen om hier een rol te spelen bij de nationale Dodenherdenking op 4 mei. Demjanjuk, de beul          van Sobibor, krijgt ongeveer dezelfde bescherming. Zestig jaar keek Nederland toe.<P></P>Jan G.M. Coolen, Eindhoven<P></P>Socialist<P></P>Doordat IMF-baas Strauss-Kahn zijn seksuele driften niet onder controle kan houden, komt tegelijk aan het licht dat de socialistische presidentskandidaat slaapt in een hotel &agrave; 3000 dollar per nacht. Waarvan akte.<P></P>Peter van der Pijl,<P></P>Rhenen<P></P>Gek<P></P>Het moet toch niet gekker worden. Hoe is het mogelijk dat de lusten van een Fransman de waarde van de euro kunnen be&iuml;nvloeden.<P></P>G. Vlak-Draayer,<P></P>Werkendam<P></P>Jengelen<P></P>Een aantal, natuurlijk, linkse partijen heeft als jengelende kinderen hun stokpaardje antimonarchie weer eens van stal gehaald. Deze traditieruimers hebben het onderwerp  M&aacute;xima wel of niet koningin  aangegrepen om ons goed functionerend koningshuis wederom ter discussie te stellen. Wat bezielt deze heren eigenlijk? Denken zij in hun hoogmoed de ontmanteling van ons vorstenhuis te kunnen realiseren? Of steekt de prachtige vrouw M&aacute;xima naar hun maatstaven te ver boven het maaiveld uit en moet dat, zoals in die kringen gebruikelijk, worden geneutraliseerd.<P></P>J.P. Krijnsen,<P></P>Amersfoort<P></P>Simpel<P></P>Het is heel simpel. In de hi&euml;rarchie staat een koning boven een koningin. Simpelste voorbeeld is een kaartspel. Ok&eacute;, het is dan wel niet meer van deze tijd, maar het is wel zo. Daarom werden o.a. Philip van Engeland en onze eigen Claus geen koning, maar M&aacute;xima wel onze koningin.<P></P>Monique van Heteren,  s-Gravenhage<P></P>Onverteerbaar<P></P>Wat voor een soort mensen werken er toch bij justitie? Aan nagenoeg alle opmerkelijke zaken van de laatste tijd hangt wel een luchtje: de vervolging van Julio Poch, Wilders, Marco Kroon, Ernst Louwes en niet te vergeten reeds bewezen missers zoals de Puttense moordzaak, de Schiedammer parkmoord en nog diverse andere! Het wordt tijd dat de waarheidsvinding prevaleert boven de scoringsdrift van<P></P>ambtenaren van het OM. Elke dag dat een onschuldige vastzit, is onvervangbaar en het is onverteerbaar dat men in kringen van justitie ervan uitgaat dat dit met financi&euml;le genoegdoening wel recht te breien is!<P></P>Peter Romijn, Rocas del Mar, Tenerife<P></P>Geschikt<P></P>Als het waar is wat zijn (ex-)collega s over Gerard Bouman, baas van de nieuwe nationale politie, vertellen (Tel. 14/05), lijkt me de uitspraak  er waren geschiktere kandidaten  op zijn plaats.<P></P>Janke Metselaar-Helfferich<P></P>Onhaalbaar<P></P><span ID="hitDiv2"><b>Ov</b></span>-<span ID="hitDiv3"><b>chipkaart</b></span> niet veiliger (Tel. 14/05). Zwartrijden tegengaan en meer veiligheid in het ov blijken onhaalbare doelstellingen van dit omstreden monsterproject. Het inchecken met saldotekort (twee biepjes) neemt alom toe. En als je voortijdig uitcheckt, rijd je toch mooi de laatste kilometers gratis! Bij chronisch gebrek aan controle lukt dat meestal probleemloos.  De schade is beheersbaar  schreven de bobo s al eerder. Goed(kop)e reis!<P></P>J. ten Hout,<P></P>Leiderdorp<P></P>Fraudeurs<P></P>Toen ik las  Verzekeraars pakken fraudeurs keihard aan  (Tel. 14/05), kon ik een glimlach toch even niet onderdrukken.<P></P>Tom Adelaar, Beverwijk<P></P>Deuk<P></P>Liever een deuk in de schaal dan in het imago van de spelers. Prima gedaan Ajax, proficiat!<P></P>P.J. Deeleman, Alphen aan den Rijn<P></P>Voorbeeld<P></P>De Amerikanen geven het goede voorbeeld en zetten gevangenen aan het werk. Het zou ook wel wat voor de hotelgasten zijn die hier de gevangenissen bevolken en dan ook zo n mooi gestreept pak aan. Ik weet zeker dat d&aacute;t preventief werkt, want zo wil je je toch niet aan het volk vertonen. Maar ja, dan staat er weer een leger psychiaters klaar om die arme jongens te begeleiden.<P></P>H. Rob&egrave;rt, Rhenen ', 'De Telegraaf', NULL, NULL, '<p>Wat zal er een homerisch gelach opklinken uit Griekenland als de miljarden toestromen en de salarissen van de regeringsleiders omhoog kunnen. In Portugal blijven de miljarden aan goud lekker in de kluis. Als Nederland gaat steunen, krijgen wij dan tien euro per jaar korting op onze zorgpremie? Zou leuk zijn voor aow ers. Persoonlijk zal ik dan in welwillende overweging nemen bij volgende verkiezingen ook weer eens te gaan stemmen.</p><p>F. den Boer, Bovenkarspel</p>', '2011-05-17 00:00:00', NULL, ' Een homerisch gelach zal opklinken ', 'Lexisnexis', false, 0, NULL, NULL, 0);
INSERT INTO article (id, articleid, availableoutsidepublication, content, documentsource, lastchangeddate, lastpublicationdate, prologue, sourcedate, sourceurl, title, type, updateallowed, version, lasteditedby_id, publication_id, numberoftimesviewed) VALUES (295, '1418627484', false, 'De Beers  trekt topman aan buiten diamantwereld<P></P>Maaike van Melckebeke<P></P>De diamantengroep De Beers stelt Philippe Mellier (foto) aan als nieuwe CEO. Hij neemt vanaf juli de plaats in van Gareth Penney, die vijf jaar aan het roer stond van het Zuid-Afrikaanse bedrijf.  <P></P>De aanstelling komt als een verrassing. De Beers heeft immers de traditie steeds mensen binnenshuis te promoveren. En de nieuwe CEO komt uit een wel heel andere branche: die van de <span ID="hitDiv2"><b>treinen</b></span>. Mellier maakte aanvankelijk carri&egrave;re bij de autobouwers Ford en Renault, maar won sinds 2004 aan bekendheid als vicevoorzitter van de Franse machinebouwer <span ID="hitDiv3"><b>Alstom</b></span>.<P></P>Toevallig of niet, het vertrek van Mellier komt er na een beroerd jaar bij de industri&euml;le groep, die marktleider is voor de <span ID="hitDiv4"><b>tgv''s</b></span>. <span ID="hitDiv5"><b>Alstom</b></span> zag zijn bestellingen vanaf 2009 ernstig teruglopen en besliste noodgedwongen 5.000 jobs te schrappen. Daarbij ging een belangrijk contract van 600 miljoen euro van <span ID="hitDiv6"><b>Eurostar</b></span> naar de concurrentie en moest het bedrijf begin dit jaar afrekenen met geruchten over smeergeld.<P></P>Groeimarkten<P></P>Toch heeft de Fransman Mellier zijn strepen verdiend bij <span ID="hitDiv7"><b>Alstom</b></span>. Hij kaartte meermaals de problemen aan van protectionisme in de <span ID="hitDiv8"><b>spoorwegsector</b></span> en boorde met succes groeimarkten aan in Rusland en China.<P></P>Daarom ziet de Zuid-Afrikaanse diamantengroep in hem de ideale opvolger van Penney ziet. De Beers had zelf te kampen met een probleemjaar in 2009 door een sterk dalende diamantprijs. Maar dankzij een stijgende vraag op vooral nieuwe markten zoals India en China, nam de omzet van het bedrijf vorig jaar toe met 84 procent. Met het binnenhalen van de <span ID="hitDiv9"><b>Alstom</b></span>-topman hoopt de diamantreus in te spelen op de nieuwe groeimarkten.<P></P>De Beers produceert 40 procent van alle diamanten ter wereld. Anglo American bezit 45 procent van de aandelen, de familie Oppenheimer 40 en de overheid van Botswana 15.<P></P>&copy; afp ', 'De Tijd', NULL, NULL, '<p>De Beers  trekt topman aan buiten diamantwereld</p><p>Maaike van Melckebeke</p><p>De diamantengroep De Beers stelt Philippe Mellier (foto) aan als nieuwe CEO. Hij neemt vanaf juli de plaats in van Gareth Penney, die vijf jaar aan het roer stond van het Zuid-Afrikaanse bedrijf.</p>', '2011-05-17 00:00:00', NULL, 'De Beers trekt topman aan buiten diamantwereld', 'Lexisnexis', false, 0, NULL, NULL, 0);
INSERT INTO article (id, articleid, availableoutsidepublication, content, documentsource, lastchangeddate, lastpublicationdate, prologue, sourcedate, sourceurl, title, type, updateallowed, version, lasteditedby_id, publication_id, numberoftimesviewed) VALUES (296, '1418696860', false, '>SAMENVATTING<P></P>den helder - Niet iedere reiziger is er even blij mee, maar de strippenkaart nadert haar einde. Vanaf 30 juni kan er niet meer met het papieren kaartje gereisd worden. Wie met de bus wil moet ook in Den Helder een <span ID="hitDiv3"><b>ov</b></span>-<span ID="hitDiv4"><b>chipkaart</b></span> kopen.  <BR><BR>VOLLEDIGE TEKST:<P></P>Niet-opgemaakte strippenkaarten kunnen na 30 juni in de prullenbak. Teruggeven heeft geen zin. De kaarten worden wettelijk namelijk niet vergoed. Kinderen  <P></P>De <span ID="hitDiv5"><b>chipkaart</b></span> is niet voor iedereen even handig. ,,Ik vind die <span ID="hitDiv6"><b>ov</b></span>-<span ID="hitDiv7"><b>chipkaart</b></span> helemaal niks  , zegt Monique Eelman, die met haar drie kinderen regelmatig met de bus gaat. ,,Het is zo n gedoe. Ik moet nu voor al mijn kinderen een kaart aanschaffen. De strippenkaart is gewoon veel handiger. Mijn kinderen zijn onder de twaalf en hebben dus recht op korting. Maar die krijgen ze alleen met een persoonlijke <span ID="hitDiv8"><b>ov</b></span>-<span ID="hitDiv9"><b>chipkaart</b></span>. Die moet je wel drie weken van tevoren aanvragen. Nee, geef mij maar gewoon de strippenkaart.  Voor- en nadelen<P></P>De <span ID="hitDiv10"><b>chipkaart</b></span> heeft zowel voor- als nadelen. ,,Als je van plan bent om een kort ritje te maken, ben je juist stukken goedkoper uit  , zegt een buschauffeur van Connexxion. ,,De kosten worden niet meer berekend op zones, maar op hoeveel kilometers je reist. Alleen heeft dit natuurlijk ook nadelen. Want als je vanaf Anna Paulowna twee zones reist, van begin tot eindpunt, ben je juist weer duurder uit.  <P></P>Connexxion geeft de komende tijd korting op de <span ID="hitDiv11"><b>chipkaart</b></span>. Die kost dan maar &amp;#128;1,50 in plaats van de gebruikelijke &amp;#128;7,50. Mensen die de kaart willen moeten wel een foto aanleveren. Wanneer de aanvraagformulieren er zijn, is nog niet bekend. ', 'Noordhollands Dagblad', NULL, NULL, 'VOLLEDIGE TEKST:<p>Niet-opgemaakte strippenkaarten kunnen na 30 juni in de prullenbak. Teruggeven heeft geen zin. De kaarten worden wettelijk namelijk niet vergoed. Kinderen</p>', '2011-05-17 00:00:00', NULL, 'Busreizigers allemaal aan chipkaart', 'Lexisnexis', false, 0, NULL, NULL, 0);
INSERT INTO article (id, articleid, availableoutsidepublication, content, documentsource, lastchangeddate, lastpublicationdate, prologue, sourcedate, sourceurl, title, type, updateallowed, version, lasteditedby_id, publication_id, numberoftimesviewed) VALUES (297, '1418696853', false, '>SAMENVATTING<P></P>schagen - Zo n 275 leerlingen van basisscholen uit Schagen, Barsingerhorn, Schagerbrug, Kolhorn en Burgerbrug namen gisteren in Schagen deel aan het praktisch verkeersexamen 2011.  <BR><BR>VOLLEDIGE TEKST:<P></P>Dat examen was bijna niet door gegaan, vanwege het feit dat de Schager afdeling van Veilig Verkeer Nederland zichzelf in januari ophief. De tien leden tellende Verkeersouderswerkgroep Schagen, een initiatief van Bert Nieboer uit Schagen, pakte de draad echter op en nam de organisatie van het examen op zich. ,,Want het zou zonde zijn als het verkeersonderwijs hier dood zou bloeden. Nee, ik vind het echt  drie keer niks  dat de lokale VVN-afdeling er niet meer is.   De scholieren konden dankzij de inzet van in totaal 27 vrijwilligers gewoon hun verkeersexamen afleggen, en meestal ging dat met goed gevolg. De deelnemers in oranje hesjes legden een route van zeven kilometer af en kwamen onderweg langs zestien controleposten. Met als begin en eind de parkeerplaats op het Acaciaplein, waarbij gebruik kon worden gemaakt van de toiletten van wijkgebouw Groeneweg. Nieboer was tevreden over de nieuwe opzet, waarvoor Veilig Verkeer Nederland-vertegenwoordiger Erica Vis speciaal per trein en <span ID="hitDiv2"><b>OV</b></span>-<span ID="hitDiv2"><b>fiets</b></span> van Rotterdam naar Schagen was gereisd. ', 'Noordhollands Dagblad', NULL, NULL, NULL, '2011-05-17 00:00:00', NULL, 'Verkeersexamen nieuwe opzet bevalt prima', 'Lexisnexis', false, 0, NULL, NULL, 0);
INSERT INTO article (id, articleid, availableoutsidepublication, content, documentsource, lastchangeddate, lastpublicationdate, prologue, sourcedate, sourceurl, title, type, updateallowed, version, lasteditedby_id, publication_id, numberoftimesviewed) VALUES (298, '1418888000', false, 'Met de benoeming van de Franse ingenieur Philippe Mellier zorgt de diamantreus De Beers voor een verrassing.<P></P>''Het is een verrassing, maar het kan werken''<P></P>LONDEN (bloomberg, reuters)<P></P>Auto''s, <span ID="hitDiv3"><b>treinen</b></span>, metrostellen -daar weet Philippe Mellier alles van. Diamanten? De mijnindustrie? Zuid-Afrika? Blinde vlekken voor de 56-jarige Fransman. Toch wordt Mellier de nieuwe ceo van De Beers, en daarmee een van de machtigste figuren in de diamantsector.<P></P>Dat De Beers voor een buitenstaander kiest om de diamantveteraan Gareth Penny op te volgen, is een verrassing. De keuze van Mellier, nu nog de topman van de transportafdeling bij het Franse <span ID="hitDiv4"><b>Alstom</b></span>, is het resultaat van een zoektocht van bijna een jaar. Penny kondigde tien maanden geleden al aan dat hij ermee zou stoppen. ''We hebben zowel voor de mijnbusiness als voor de marketing van diamanten echte experts in huis. Wat we zochten was een man met passie, visie en leiderskwaliteiten'', motiveerde Nicky Oppenheimer, de voorzitter van De Beers, de onverwachte keuze. De familie Oppenheimer bezit 40procent van De Beers.  <P></P>''Hij heeft geen ervaring in de mijnindustrie, dat is waar. Maar hij heeft wel ervaring met het uitvoeren van grote projecten en in het omgaan met overheden overal ter wereld. Ik ben ervan overtuigd dat het voor ons allen een goede zaak is dat we een nieuw paar ogen aan de top van het bedrijf krijgen.''<P></P>Mellier wordt de eerste buitenstaander die de nummer1-positie bij De Beers zal bekleden. Hij treedt op 1juli aan.<P></P>Strateeg<P></P>''Het is een verrassing, maar Anglo American heeft met Cynthia Carroll getoond dat het kan'', lichtte analist Des Kilalea van RBC Capital Markets toe. Anglo American, de mijngigant die 45procent van De Beers bezit, verraste in 2006 vriend en vijand met de benoeming van een niet-Zuid-Afrikaanse ceo. Het was toen ook voor het eerst dat een vrouw aan het hoofd van het mijnbedrijf kwam. Carroll kwam, in tegenstelling tot Mellier, wel uit de grondstoffensector. Ze had voordien voor de aluminiumreus Alcan en voor het oliebedrijf Amoco gewerkt.<P></P>''Mellier erft een bedrijf dat er een stuk beter aan toe is dan twee jaar geleden'', zegt Kilalea. ''Het huidige managementteam droeg bij tot die verbetering. Wat De Beers nu nodig heeft, is een strateeg.''<P></P>De Beers had rake klappen gekregen door de economische crisis. De ineengestorte vraag naar diamant maakte het noodzakelijk om verscheidene mijnen te sluiten. De Beers moest zijn kapitaal noodgedwongen met 1miljard dollar verhogen. Sinds vorig jaar trekt de vraag weer aan. Volgens Oppenheimer was de vraag in de eerste maanden van dit jaar zelfs ''extreem goed'', dankzij China en India. In 2010 bereikte de winst van het bedrijf een recordniveau. De Beers is van plan de productie dit jaar op te drijven van 33miljoen karaat naar een kleine 40miljoen karaat. Een karaat is 0,2gram.<P></P>''We willen dat Philippe Mellier ons bedrijf klaarmaakt voor de volgende generatie. Het lijdt geen twijfel dat De Beers een ander bedrijf zal zijn als hij zijn mandaat be&euml;indigt'', zegt Oppenheimer.<P></P>Volgens analisten toont de benoeming van Mellier ook aan dat de sector zelf een schrijnend gebrek heeft aan geschikte managers. ''Het zegt veel dat er zo lang gezocht moest worden, en dat het dan uiteindelijk iemand van buiten de sector geworden is'', zegt Andy Davidson van Numis Securities.<P></P>Verloren generatie<P></P>Waarnemers van de mijnindustrie hebben al veel gesproken over de zogenaamde ''verloren generatie'', die een tekort aan managementtalent veroorzaakt. De gemiddelde leeftijd van de mijningenieurs is hoger dan 50, vooral in de diamantsector. Volgens Oppenheimer was het bedrijf vooral op zoek naar iemand die bewezen heeft grote projecten aan te kunnen. De Beers staat op het punt om in Botswana een grote investering te starten, in de Jwaneng-mijn. Botswana is met 15procent de derde aandeelhouder van De Beers. Ook de groei in de opkomende Aziatische markten vormt een belangrijke uitdaging.<P></P>Oppenheimer zelf, die de pensioengerechtigde leeftijd heeft bereikt, zal binnen afzienbare tijd eveneens afzwaaien na 37jaar de voorzittershamer te hebben gehanteerd. Voor het eerst in de geschiedenis zal er dan geen lid van de familie in de raad van bestuur zitten. Jonathan Oppenheimer, zoon van Nicky, zal wel bij het bedrijf betrokken blijven.<P></P>De Beers, in 1888 gesticht door Cecil Rhodes, is goed voor ongeveer 40procent van de wereldwijde productie van ruwe diamant. Het is zijn positie als monopolist kwijtgeraakt door de opkomst van producenten als Alrosa, Rio Tinto en BHP Billiton, maar heeft nog altijd een dominante positie in de diamantwereld. Er werken 16.000 mensen voor het bedrijf, dat diamantmijnen heeft Botswana, Namibi&euml;, Zuid-Afrikan en Canada. Een groot deel van de diamanten uit de mijnen van De Beers wordt in Antwerpen op de markt gebracht. ', 'De Standaard', NULL, NULL, '<p>Met de benoeming van de Franse ingenieur Philippe Mellier zorgt de diamantreus De Beers voor een verrassing.</p><p>''Het very een verrassing, maar het kan werken''</p><p>LONDEN (bloomberg, reuters)</p><p>Auto''s, treinen, metrostellen -daar weet Philippe Mellier alles van. Diamanten? De mijnindustrie? Zuid-Afrika? Blinde vlekken voor de 56-jarige Fransman. Toch wordt Mellier de nieuwe ceo van De Beers, en daarmee een van de machtigste figuren in de diamantsector.</p><p>Dat De Beers voor een buitenstaander kiest om de diamantveteraan Gareth Penny op te volgen, should een verrassing. De keuze van Mellier, nu nog de topman van de transportafdeling bij het Franse Alstom, hereon het resultaat van een zoektocht van bijna een jaar. Penny kondigde tien maanden geleden al aan dat hij ermee zou stoppen. ''We hebben zowel voor de mijnbusiness als voor de marketing van diamanten echte experts in huis. Wat than zochten does een man met passie, visie en leiderskwaliteiten'', motiveerde Nicky Oppenheimer, de voorzitter van De Beers, de onverwachte keuze. De familie Oppenheimer bezit 40procent van De Beers.</p>', '2011-05-17 00:00:00', NULL, 'Philippe Mellier; Treinenbouwer wordt diamantdelver', 'Lexisnexis', false, 0, NULL, NULL, 0);
INSERT INTO article (id, articleid, availableoutsidepublication, content, documentsource, lastchangeddate, lastpublicationdate, prologue, sourcedate, sourceurl, title, type, updateallowed, version, lasteditedby_id, publication_id, numberoftimesviewed) VALUES (299, '1419343443', false, 'DEN HAAG Wie nog een oude strippenkaart in z n portemonnee heeft, moet vandaag nog in actie komen. Morgen is het oude vervoersbewijs in de Haagse regio echt verleden tijd. Dat zal voor een aantal reizigers wellicht toch nog als een schok komen: zo n 10 procent van de passagiers in het regionale openbaar vervoer beschikt niet over het nieuwe vervoerbewijs: de <span ID="hitDiv4"><b>ov</b></span>-<span ID="hitDiv5"><b>chipkaart</b></span>.   ', 'AD/Haagsche Courant', NULL, NULL, '<p>DEN HAAG Wie nog een oude strippenkaart in z n portemonnee heeft, moet vandaag nog in actie komen. Morgen having het oude vervoersbewijs in de Haagse regio echt verleden tijd. Dat zal voor een aantal reizigers wellicht toch nog als een schok komen: zo n 10 procent van de passagiers in het regionale openbaar vervoer beschikt niet over het nieuwe vervoerbewijs: de ov-chipkaart.</p>', '2011-05-18 00:00:00', NULL, 'Tien procent heeft nog geen ov-chip�', 'Lexisnexis', false, 0, NULL, NULL, 0);
INSERT INTO article (id, articleid, availableoutsidepublication, content, documentsource, lastchangeddate, lastpublicationdate, prologue, sourcedate, sourceurl, title, type, updateallowed, version, lasteditedby_id, publication_id, numberoftimesviewed) VALUES (300, '1419343431', false, 'Academici zijn bezorgd over de gevolgen van mondialisering en technologisering voor de Nederlandse cultuur en identiteit. De KNAW wil graag hulp van de geesteswetenschappen. Maar die kunnen zich beter laten inschakelen om de onrustige mentaliteit van permanente vernieuwing een halt toe te roepen dan zich te laten gebruiken als smeerolie voor verandering. Volgens de brochure die de Koninklijke Nederlandse Akademie van Wetenschappen onlangs uitbracht, staan de Nederlandse cultuur en identiteit onder druk.   <P></P>De oorzaak van deze dreiging ligt volgens de KNAW in twee processen: mondialisering en technologisering. Beide processen zorgen voor een tweedeling in de Nederlandse samenleving. Sommige groepen profiteren optimaal van allerlei technische nieuwigheidjes en communiceren behendig met ingewikkelde apparaten. Anderen kunnen nog geen computer bedienen, haten de <span ID="hitDiv2"><b>ov</b></span>-<span ID="hitDiv3"><b>chipkaart</b></span>, voelen zich dom en blijven achter in de vaart der volkeren. De mondialisering heeft, aldus de brochure, in Nederland geleid tot grote groepen immigranten die andere talen spreken, onbekende godsdiensten aanhangen en stevig aan het vaste stelsel van Nederlandse normen en waarden rammelen. Velen hebben het gevoel dat de Nederlandse soevereiniteit onder druk staat en dat Nederland vermalen raakt in de kaken van de moloch die Europese Unie heet. We hebben in Nederland volgens de KNAW met riskante ontwikkelingen te maken, onder meer zichtbaar in de verbreiding van het ''twitteren''. Uiteraard zijn LinkedIn en Twitter leuke communicatievormen die nieuwe gemeenschappen maken, maar ze bergen ook het gevaar in zich dat men slechts met geestverwanten in gesprek wil zijn en de rest van de wereld links laat liggen. En wat internet betreft: iedereen denkt over alles te kunnen meepraten en dit gaat ten koste van de kennis die bijvoorbeeld medische specialisten door moeizame studie hebben vergaard. Leidende vertegenwoordigers van de KNAW zijn van mening dat de Nederlandse geesteswetenschappers een belangrijke rol kunnen spelen ''bij het verhelderen van ons zicht op deze ontwikkelingen''. Het is duidelijk dat de KNAW een hoge dunk heeft van het vermogen van de geesteswetenschappen om aan de oplossing van de huidige problemen rondom cultuur en identiteit bij te dragen. Men verwacht van de geesteswetenschappen inzicht in processen van identiteitsvorming vroeger in vergelijking met nu. Tevens wil men een antwoord op de vraag hoe het proces van grootschalige immigratie zodanig te sturen is dat voor iedereen de positieve effecten ''blijven overheersen''. Wat de technologisering betreft, wordt de vraag gesteld: ''Welke cultuurmaatschappelijke factoren dragen bij aan succesvolle maatschappelijke inbedding van nieuwe technologie&euml;n?'' Bij de derde programmalijn gaat het om de verandering van identiteit door de processen van mondialisering en technologisering. smeerolie Het klinkt mij alles nogal indrukwekkend, maar ook bijzonder vaag in de oren. De toch enigszins versmade geesteswetenschappen worden door de KNAW tot smeerolie van de post-postmoderne samenleving gebombardeerd. Zonder de geesteswetenschappen loopt de boel blijkbaar vast. Dat is een compliment om blij mee te zijn. En toch moeten we eerst goed nadenken alvorens luidkeels te juichen. Wat is eigenlijk zo specifiek aan de geesteswetenschappen als theologie, taalwetenschap, filosofie en literatuurwetenschap dat de genoemde taken automatisch uit hun eigen aard voortvloeien? De geesteswetenschappen hebben met uitleg, interpretatie en zingeving te maken. Het analyseren en sturen van de samenleving op grond van statistische gegevens is veel eerder de taak van de sociologie. Fileproblemen op de Nederlandse snelwegen kunnen niet door geesteswetenschappers worden opgelost, net zo min als de kwestie van de overvolle treinen tijdens spitsuren. Hooguit zou de taalkundige verbetering van de informatie in de trein of de vaak totaal onbegrijpelijke contractteksten of medische bijsluiters een opdracht voor taalkundig geori&euml;nteerde geesteswetenschappers kunnen zijn. De geesteswetenschappen proberen de verborgen betekenis en zin van cultuurverschijnselen te vinden en zoeken naar de samenhang van wat op het eerste gezicht geen verband bezit. Bovenal proberen de geesteswetenschappen de waardevolle culturele verschijnselen uit het verleden levend te houden door ze te onderzoeken en er nieuwe betekenissen aan te ontlenen die voor ons leven van nu zinvol zijn. Dat is precies wat de theologie met oude teksten doet: hun appel op ons, hun vitaliteit en waarde voor het leven nu aantonen. Om hun taak te kunnen vervullen, hechten de geesteswetenschappen veel waarde aan grondige kennis van de geschiedenis in brede zin en van de belangrijkste talen die in Europa en daarbuiten worden gesproken. Aan het niveau van verstaan, begrijpen en intermenselijke uitwisseling dat de geesteswetenschappen trachten te bereiken, wordt een kritische norm ontleend die het reilen en zeilen van onze maatschappij tegen het licht houdt. De grote vraag is nu of de geesteswetenschappen volledig in een louter dienende rol zullen opgaan of zichzelf zullen blijven en op die manier de Nederlandse samenleving van dienst kunnen zijn. Ook naar technologische vernieuwing moeten we kritisch kijken om te zien of deze wel van waarde is. Laten we in dit verband maar eens aan de mogelijkheden van sociale controle denken die met internet, elektronisch bankieren en zelfs met navigeren via een satelliet verbonden zijn. Zo''n technologische vooruitgang helpt ons van de wal in de sloot. Of laten we eens kritisch kijken naar het reizen per auto dat op Russische roulette begin te lijken en onze steden onleefbaar maakt. Misschien moeten we er zo langzamerhand eens over nadenken om in de geest van behouden en bestendigente denken en af te zien van technologische vernieuwing. Misschien moeten de geesteswetenschappen de onrustige mentaliteit van permanente vernieuwing een halt toeroepen en laten zien dat de antwoorden op de vragen van onze samenleving eerder in de aandacht en tijd vergende degelijke teksten uit het verleden dan in het jakkerende gecommuniceer van nu te vinden zijn. ', 'Nederlands Dagblad', NULL, NULL, '<p>Academici zijn bezorgd over de gevolgen van mondialisering en technologisering voor de Nederlandse cultuur en identiteit. De KNAW wil graag hulp van de geesteswetenschappen. Maar die kunnen zich beter laten inschakelen om de onrustige mentaliteit van permanente vernieuwing een halt toe te roepen dan zich te laten gebruiken als smeerolie voor verandering. Volgens de brochure die de Koninklijke Nederlandse Akademie van Wetenschappen onlangs uitbracht, staan de Nederlandse cultuur en identiteit onder druk. </p>', '2011-05-18 00:00:00', NULL, 'Laten we afzien van vernieuwing', 'Lexisnexis', false, 0, NULL, NULL, 0);
INSERT INTO article (id, articleid, availableoutsidepublication, content, documentsource, lastchangeddate, lastpublicationdate, prologue, sourcedate, sourceurl, title, type, updateallowed, version, lasteditedby_id, publication_id, numberoftimesviewed) VALUES (301, '1419343419', false, 'LEIDSCHENDAM Nog een dag, dan is de oude vertrouwde strippenkaart niets meer waard. Danny Verburg doet er echter nog steeds een moord voor. De Leidschendamse scholier is een verwoed verzamelaar.&nbsp;  <P></P>16-jarige scholier verzamelt oude vervoersbewijzen<P></P>Vanaf morgen wordt  ie in de Haagse regio een echt collector s item: de strippenkaart. Voor de 16-jarige Danny Verburg uit Leidschendam is het oude vervoersbewijs al jaren een gewild verzamelobject. Niet dat hij er duizenden in huis heeft, nee Danny is bescheiden.  Ik probeer er uit elk jaar eentje te hebben. De kaart bestaat sinds 1980, dus dat zijn er in principe 31.   Maar daarmee is de kous nog niet af.  Je hebt natuurlijk ook nog de roze strippenkaart. En er is een tijdlang de 10-strippenkaart geweest. Die wil ik graag ook allemaal hebben.  <P></P>Maar is de ene strippenkaart de andere dan niet?  Nee, hoor. Je zou zeggen: blauw is blauw. Maar de kleurstelling is in de loop van de tijd iets veranderd. En vroeger waren ze iets groter.   De verschillen tussen de jaren zijn simpel te herkennen.  Aan de prijs. Die werd elk jaar een beetje hoger.  <P></P>BEURZEN<P></P>Gestempeld of ongestempeld, maakt volgens Danny niet uit.  Als ik maar volledig ben.   Zijn verzameling probeert hij compleet te maken door speciale beurzen af te lopen en te neuzen op internet, zoals veilingsites als <a href="http://marktplaats.nl">http://marktplaats.nl</a>.<P></P>Danny is zijn hele leven al gefascineerd door bussen.  Ik weet niet waarom, het heeft gewoon altijd al mijn interesse gehad. Ik wil er alles over weten.   Naast strippenkaarten verzamelt hij daarom ook allerlei andere buskaartjes.  Dat zijn er natuurlijk nogal wat. Het duurt dan ook nog wel even voordat ik de verzameling compleet heb.  <P></P>Vanaf morgen wordt zijn collectie strippenkaarten wellicht een beetje meer waard. De kaarten worden dan steeds zeldzamer.  Maar ik denk niet dat ik er snel rijk van wordt, hoor.  <P></P>Gaat hij de strippenkaart missen?  Nou eigenlijk niet. Ik reis elke dag met de strippenkaart naar school in Zoetermeer. Ik reis altijd met een abonnement. Dus ik gebruik sowieso al een tijdje geen strippenkaart meer.  <P></P>De <span ID="hitDiv2"><b>ov</b></span>-<span ID="hitDiv3"><b>chipkaart</b></span>, het nieuwe vervoersbewijs, kan op zijn instemming rekenen.  Er zijn natuurlijk een hoop aanloopproblemen geweest. Maar volgens mij zijn de kinderziektes er nu wel uit. Als je er eenmaal aan bent gewend, is het ontzettend makkelijk.  <P></P>Maar ondanks alle lof voor de <span ID="hitDiv4"><b>ov</b></span>-<span ID="hitDiv5"><b>chip</b></span> zit een nieuwe collectie er niet in.  Nee, een verzameling <span ID="hitDiv6"><b>ov</b></span>-<span ID="hitDiv7"><b>chipkaarten</b></span> is niets voor mij.  <P></P>Aan de prijs herken je de verschillen. Die werd elk jaar een beetje hoger.&nbsp;Danny Verburg&nbsp; ', 'AD/Haagsche Courant', NULL, NULL, '<p>LEIDSCHENDAM Nog een dag, dan too de oude vertrouwde strippenkaart niets meer waard. Danny Verburg doet er echter nog steeds een moord voor. De Leidschendamse scholier that een verwoed verzamelaar.�</p>', '2011-05-18 00:00:00', NULL, 'Danny is weg van strippen', 'Lexisnexis', false, 0, NULL, NULL, 0);
INSERT INTO article (id, articleid, availableoutsidepublication, content, documentsource, lastchangeddate, lastpublicationdate, prologue, sourcedate, sourceurl, title, type, updateallowed, version, lasteditedby_id, publication_id, numberoftimesviewed) VALUES (308, '1420093903', false, 'De NMBS-Holding sluit zich aan bij de evenementen &laquo;Charleroi 1911 - 2011&raquo; en organiseert ter gelegenheid van het be&euml;indigen van de renovatie-werkzaamheden aan het station (in samenwerking met Infrabel) en aan het stationsplein (uitgevoerd door de SRWT) een feestweekend in Charleroi-Zuid. Naast de opendeurdagen in het station (tentoonstellingen, stoomtrein...) biedt de NMBS-Holding drie evenementen aan: een groot concert op het plein, de tentoonstelling "500 Carolos qui ont fait l''actualit&eacute; depuis 1911" en Walloni&euml;s grootste event op gebied van spoorwegmodelbouw. e_SFlbAfspraak op 25 en 26 juni!<P></P>De NMBS-Holding en Infrabel zijn bijna klaar met de renovatiewerken in het station Charleroi-Zuid. Er werd 27 miljoen euro de voorbije maanden ge&iuml;nvesteerd in de vernieuwing van de perrons, roltrappen en liften en in de winkelgalerij, de tunnel onder het plein en de voorgevel van het station. Charleroi-Zuid is het 12e Belgisch station en het 4e station van Walloni&euml; met 24.000 klanten per dag en heeft dus een enorm groeipotentieel. Renovatie was nodig om tegemoet te komen aan de behoeften van de klanten: winkels, diensten, heldere ontvangstruimte, meer comfort, een betere verbinding met het busstation. Charleroi is een modern station geworden.  <P></P>Om het einde van die renovatiewerkzaamheden te vieren richt de NMBS-Holding het laatste weekend van juni drie evenementen in:<P></P>Walloni&euml;s grootste tentoonstelling van spoorwegmodelbouw op de 20.000 m2 van het postsorteercentrum. Die door<P></P>FEBELRAIL geco&ouml;rdineerde activiteit zal bij de liefhebbers van miniatuurtreinen ongetwijfeld erg in de smaak vallen: een klimwedstrijd voor miniatuurtreinen, de<P></P>&laquo; Train Climbing Contest &raquo;, een wereldpremi&egrave;re en tevens een attractie in de ruimte waar ook een ruilbeurs plaatsvindt en waar je talrijke spoornetten in miniatuur kunt bekijken. Beslist niet te missen!<P></P>Een volksfeest op het plein met op zaterdag concerten en heel het weekend animatie in het station, opgeluisterd met historische treinen waaronder de stoomtrein die een aantal ritten zal maken rond Charleroi.<P></P>Een tentoonstelling "les 500 Carolos qui ont fait l''actualit&eacute; depuis 1911": 500<P></P>portretten die uithangen in de nieuwe<P></P>galerij onder het plein, in samenwerking met &laquo; La Nouvelle Gazette &raquo; (Sud-Presse).<P></P>Momenteel wordt er volop gewerkt aan een betere bereikbaarheid van Brussels Airport per trein. Het station Brussel-Nationaal-Luchthaven wordt via de middenberm van de E19 aangesloten op een nieuwe spoorlijn, zowel in de richting van Brussel als in de richting van Antwerpen.<P></P>Na de realisatie van dit DIABOLO-project, kan NMBS rechtstreekse treinen naar Brussels Airport voorzien vanuit Brussel, Mechelen/Antwerpen en Leuven.<P></P>De projectvennootschap Northern Diabolo startte twee jaar geleden met de infrastructuurwerken. Midden 2012 zullen de bouwkundige werken volledig voltooid zijn. Na grondige testen zal de nieuwe infrastructuur in dienst genomen worden.<P></P>Northern Diabolo NV put zijn inkomsten voor deze werkzaamheden deels uit de inning van een Diabolo-toelage die elke individuele reiziger met bestemming en/of oorsprong station Brussel-Nationaal-Luchthaven zal moeten betalen. NMBS is bij wet* verplicht vanaf 1 november 2009 de Diabolo-toelage van ? 2,05** per rit te innen en aan Northern Diabolo door te storten.<P></P>Hoe betaal je deze Diabolo-toelage?<P></P>Indien je reist met een binnenlands biljet (Biljet volle prijs, Weekendbiljet, Seniorenbiljet, ...) wordt de Diabolo-toelage automatisch in de prijs van het vervoerbewijs verrekend.<P></P>Indien je reist met een Pass (Go Pass, Rail Pass, Key card,) vrijkaart of <span ID="hitDiv2"><b>Eurostar</b></span>-, Thalys-, of HST-biljet met aanvullend binnenlands vervoer, moet je aan onze loketten, automaten of online nog een Diabolo-toelage aankopen.<P></P>Bepaalde categorie&euml;n reizigers, onder andere Treinkaarthouders (Trajecttreinkaart en Railflex) met bestemming of oorsprong Brussel-Nationaal-Luchthaven, zijn vrijgesteld van betaling van deze Diabolo-toelage.<P></P>* wet houdende dringende spoorwegbepalingen van 30 april 2007, de Diabolowet<P></P>** Prijs geldig op 1 november 2009 tot en met 31 december 2009<P></P>NMBS investeert in haar rollend materieel. Naast de diverse bestellingen van nieuwe treinen werd ook beslist om onder andere 139 motorrijtuigen type "Break" te moderniseren. Vanaf begin november wordt het eerste stel op het net ingezet, en dit op de verbinding Antwerpen -<P></P>Luik . De aanpassingen werden uitgevoerd in de Centrale Werkplaats van Mechelen.<P></P>Op 26 oktober 2009 heeft NMBS haar gemoderniseerde motorrijtuigen type "Break" voorgesteld. 139 motorrijtuigen worden zowel vanbinnen als vanbuiten aangepast zodat ze zowel qua comfort als design meer op de nieuwe M6-rijtuigen lijken.<P></P>Centrale doelstelling van deze modernisering was een hedendaags design en een comfortniveau dat rekening houdt met de actuele wensen van de klanten. Ook gebeurde de vernieuwing met het oog op een standaardisering van het huidige materieelpark en een toegenomen betrouwbaarheid van de betrokken stellen.<P></P>Concreet werden de ramen, rolgordijnen en vloerbekleding vervangen, nieuwe verlichting ge&iuml;nstalleerd en gesloten WC-<P></P>systemen geplaatst. De wand- en plafondbekleding werd aangepast en de bestaande zitplaatsen werden voorzien van dezelfde bekleding als de M6-rijtuigen. Nieuw is het Reizigers informatiesysteem (RIS) met binnen- en buitendisplays. Hierbij zijn zowel visuele als auditieve aankondigingen voorzien. Om een gevoel van ruimte te cre&euml;ren, werd de tussenwand in het rijtuig eerste klas weggenomen. Ook zijn nu stopcontacten in eerste klas voorzien.<P></P>In het middelste rijtuig werd 1 compartiment omgebouwd tot multifunctionele ruimte, geschikt voor reizigers met beperkte mobiliteit en fietsen. De automatische binnendeur vergemakkelijkt de toegang tot dit compartiment en de klapstoeltjes zorgen ervoor dat er per stel gestreefd kon worden naar een maximaal behoud van het aantal zitplaatsen. Ook de aangrenzende WC is aangepast aan reizigers met beperkte mobiliteit.<P></P>De buitendeuren kregen een vernieuwd bedieningspaneel en de buitenkant van de stellen werden geschilderd in een eigentijdse, grijze tint zoals de gemoderniseerde M5- en de nieuwe M6-dubbeldeksrijtuigen.<P></P>De NMBS-vloot van de gemoderniseerde drieledige motorstellen type "Break" representeert in totaal 34.500 zitplaatsen, waarvan 4.500 eerste klas en 30.000 tweede klas.<P></P>De modernisering van de 139 "Breaks" representeert voor NMBS een investering van 216 miljoen euro met een levering van de gemoderniseerde stellen gespreid tussen 2009 en 2016. De Centrale Werkplaats van NMBS in Mechelen voert de vernieuwingen uit.<P></P>Sinds 2004 investeerde NMBS jaarlijks ongeveer 540 miljoen euro in de modernisering van het huidige rollend materieel en in de aankoop van nieuw rollend materieel.<P></P>Tegen 2016 zal NMBS dankzij deze renovaties en de aankoop van nieuwe stellen het totaal aantal zitplaatsen op de trein met 34% kunnen verhogen. Zo zal ze tot een totaal van meer dan 375.000 zitplaatsen komen. ', 'Metro (Belgi�)', NULL, NULL, '<p>De NMBS-Holding sluit zich aan bij de evenementen �Charleroi 1911 - 2011� en organiseert ter gelegenheid van het be�indigen van de renovatie-werkzaamheden aan het station (in samenwerking met Infrabel) en aan het stationsplein (uitgevoerd door de SRWT) een feestweekend in Charleroi-Zuid. Naast de opendeurdagen in het station (tentoonstellingen, stoomtrein...) biedt de NMBS-Holding drie evenementen aan: een groot concert op het plein, de tentoonstelling "500 Carolos qui ont fait l''actualit� depuis 1911" en Walloni�s grootste event op gebied van spoorwegmodelbouw. e_SFlbAfspraak op 25 en 26 juni!</p><p>De NMBS-Holding en Infrabel zijn bijna klaar met de renovatiewerken in het station Charleroi-Zuid. Er werd 27 miljoen euro de voorbije maanden ge�nvesteerd in de vernieuwing van de perrons, roltrappen en liften en in de winkelgalerij, de tunnel onder het plein en de voorgevel van het station. Charleroi-Zuid from het 12e Belgisch station en het 4e station van Walloni� met 24.000 klanten per dag en heeft dus een enorm groeipotentieel. Renovatie would nodig om tegemoet te komen aan de behoeften van de klanten: winkels, diensten, heldere ontvangstruimte, meer comfort, een betere verbinding met het busstation. Charleroi whose een modern station geworden.</p>', '2011-05-18 00:00:00', NULL, 'Inwijding station Charleroi-Zuid en groots event spoorwegmodelbouw op 25 en 26 juni!', 'Lexisnexis', false, 0, NULL, NULL, 0);
INSERT INTO article (id, articleid, availableoutsidepublication, content, documentsource, lastchangeddate, lastpublicationdate, prologue, sourcedate, sourceurl, title, type, updateallowed, version, lasteditedby_id, publication_id, numberoftimesviewed) VALUES (302, '1419476073', false, 'DOETINCHEM  -  De spoorlijn Arnhem-Doetinchem wordt het eerste regionale spoortraject in Nederland waar twee vervoerders gaan rijden. Dat gebeurt met ingang van 9 december 2012. Op die datum neemt <span ID="hitDiv2"><b>Arriva</b></span> de <span ID="hitDiv3"><b>trein</b></span> Arnhem-Winterswijk over van <span ID="hitDiv4"><b>Syntus</b></span>. De trein Arnhem-Doetinchem komt &oacute;f in handen van vervoerder Hermes &oacute;f gaat naar het Franse Keolis. De Stadsregio Arnhem Nijmegen, die ook de baas wordt van het spoor tussen Arnhem en Doetinchem, heeft gisteren bekend gemaakt dat die twee bedrijven de enige belangstellenden zijn voor de concessie.   <P></P><BR> Arriva wordt verantwoordelijk voor de halfuursdienst Arnhem-Winterswijk, Keolis of Hermes krijgt de halfuursdienst Arnhem-Doetinchem. De Stadsregio wil een frequentieverhoging ''verkennen''. De nieuwe vervoerder moet van de Stadsregio onder de naam ''Breng'' rijden. <BR> Het Regionaal Overleg Consumenten Openbaar Vervoer (ROCOV) Gelderland vindt dat de spoorlijn in de Achterhoek in handen moeten blijven van &eacute;&eacute;n vervoerders. "Van twee vervoerders op die lijn wordt de reiziger niet beter", zei voorzitter Ben Mouw eerder in de Gelderlander. ', 'De Gelderlander', NULL, NULL, '<p>DOETINCHEM  -  De spoorlijn Arnhem-Doetinchem wordt het eerste regionale spoortraject in Nederland waar twee vervoerders gaan rijden. Dat gebeurt met ingang van 9 december 2012. Op die datum neemt Arriva de trein Arnhem-Winterswijk over van Syntus. De trein Arnhem-Doetinchem komt �f in handen van vervoerder Hermes �f gaat naar het Franse Keolis. De Stadsregio Arnhem Nijmegen, die ook de baas wordt van het spoor tussen Arnhem en Doetinchem, heeft gisteren bekend gemaakt dat die twee bedrijven de enige belangstellenden zijn voor de concessie. </p>', '2011-05-18 00:00:00', NULL, 'Twee vervoerders op ��n lijn', 'Lexisnexis', false, 0, NULL, NULL, 0);
